server/activecampaign.ts.content:
interface ActiveCampaignContact {
  email: string;
  firstName?: string;
  lastName?: string;
  phone?: string;
  fieldValues?: Array<{
    field: string;
    value: string;
  }>;
}

interface ActiveCampaignResponse {
  contact?: {
    id: string;
    email: string;
  };
  contacts?: Array<{
    id: string;
    email: string;
  }>;
  tags?: Array<{
    id: string;
    tag: string;
  }>;
  tag?: {
    id: string;
    tag: string;
  };
}

class ActiveCampaignService {
  private apiUrl: string;
  private apiKey: string;
  private listId: string;

  constructor() {
    if (!process.env.ACTIVECAMPAIGN_API_URL) {
      throw new Error('ACTIVECAMPAIGN_API_URL environment variable is required');
    }
    if (!process.env.ACTIVECAMPAIGN_API_KEY) {
      throw new Error('ACTIVECAMPAIGN_API_KEY environment variable is required');
    }
    if (!process.env.ACTIVECAMPAIGN_LIST_ID) {
      throw new Error('ACTIVECAMPAIGN_LIST_ID environment variable is required');
    }

    this.apiUrl = process.env.ACTIVECAMPAIGN_API_URL;
    this.apiKey = process.env.ACTIVECAMPAIGN_API_KEY;

    // Extract list ID from URL if it's a full URL
    const listIdEnv = process.env.ACTIVECAMPAIGN_LIST_ID;
    if (listIdEnv.includes('listid=')) {
      // Extract numeric ID from URL like "https://kgprofessional.activehosted.com/app/contacts?listid=15&status=1"
      const match = listIdEnv.match(/listid=(\d+)/);
      this.listId = match ? match[1] : listIdEnv;
    } else {
      this.listId = listIdEnv;
    }

    console.log(`ActiveCampaign initialized with list ID: ${this.listId}`);
  }

  private async makeRequest(endpoint: string, method: string = 'GET', data?: any): Promise<any> {
    const url = `${this.apiUrl}/api/3/${endpoint}`;

    const options: RequestInit = {
      method,
      headers: {
        'Api-Token': this.apiKey,
        'Content-Type': 'application/json',
      },
    };

    if (data && (method === 'POST' || method === 'PUT')) {
      options.body = JSON.stringify(data);
    }

    // Log important requests for debugging
    if (method === 'POST') {
      console.log(`ActiveCampaign ${method} request to: ${url}`);
    }

    try {
      const response = await fetch(url, options);

      if (!response.ok) {
        const errorText = await response.text();
        console.error(`ActiveCampaign API Error: ${response.status} - ${errorText}`);
        console.error(`Failed request URL: ${url}`);
        console.error(`Failed request method: ${method}`);
        if (data) {
          console.error(`Failed request payload:`, JSON.stringify(data, null, 2));
        }
        throw new Error(`ActiveCampaign API request failed: ${response.status} - ${errorText}`);
      }

      const responseData = await response.json();
      return responseData;
    } catch (error) {
      console.error('ActiveCampaign API Error:', error);
      throw error;
    }
  }

  async addContact(contactData: ActiveCampaignContact): Promise<string | null> {
    try {
      // First, check if contact already exists
      const existingContact = await this.findContactByEmail(contactData.email);

      if (existingContact) {
        console.log(`Contact already exists in ActiveCampaign: ${contactData.email} (ID: ${existingContact})`);
        await this.addContactToList(existingContact, this.listId);

        // Apply the salonsuccessmanager tag to existing contact
        await this.applyTagToContact(existingContact, 'salonsuccessmanager');

        return existingContact;
      }

      // Create new contact with list subscription
      console.log(`Creating new contact for: ${contactData.email}`);
      const contactPayload = {
        contact: {
          email: contactData.email,
          firstName: contactData.firstName || '',
          lastName: contactData.lastName || '',
          phone: contactData.phone || '',
          fieldValues: contactData.fieldValues || [],
          p: [this.listId] // Subscribe to list during creation
        }
      };

      const response: ActiveCampaignResponse = await this.makeRequest('contacts', 'POST', contactPayload);

      if (response.contact?.id) {
        console.log(`Contact created in ActiveCampaign: ${contactData.email} (ID: ${response.contact.id})`);

        // Apply the salonsuccessmanager tag
        await this.applyTagToContact(response.contact.id, 'salonsuccessmanager');

        // Note: List assignment handled manually in ActiveCampaign
        console.log(`Contact ready for list assignment: https://kgprofessional.activehosted.com/app/contacts/view/${response.contact.id}`);

        return response.contact.id;
      }

      return null;
    } catch (error) {
      console.error('Error adding contact to ActiveCampaign:', error);
      return null;
    }
  }

  private async findContactByEmail(email: string): Promise<string | null> {
    try {
      const response: ActiveCampaignResponse = await this.makeRequest(`contacts?email=${encodeURIComponent(email)}`);

      if (response.contacts && response.contacts.length > 0) {
        return response.contacts[0].id;
      }

      return null;
    } catch (error) {
      console.error('Error finding contact by email:', error);
      return null;
    }
  }

  private async addContactToList(contactId: string, listId: string): Promise<void> {
    const subscriptionPayload = {
      contactList: {
        list: listId,
        contact: contactId,
        status: 1
      }
    };

    try {
      // Add a small delay to ensure contact is fully created
      await new Promise(resolve => setTimeout(resolve, 1000));

      const response = await this.makeRequest('contactLists', 'POST', subscriptionPayload);
      console.log(`Contact ${contactId} successfully added to list ${listId}`);
      return;
    } catch (error) {
      console.error(`Failed to add contact ${contactId} to list ${listId}:`, error);
      // Try one more time after delay
      try {
        await new Promise(resolve => setTimeout(resolve, 2000));
        const response = await this.makeRequest('contactLists', 'POST', subscriptionPayload);
        console.log(`Contact ${contactId} added to list ${listId} on retry`);
      } catch (retryError) {
        console.error(`Retry failed for contact ${contactId}:`, retryError);
        // Don't throw - contact creation is more important
      }
    }
  }

  private async findOrCreateTag(tagName: string): Promise<string | null> {
    try {
      // First, try to find existing tag
      const response: ActiveCampaignResponse = await this.makeRequest(`tags?search=${encodeURIComponent(tagName)}`);

      if (response.tags && response.tags.length > 0) {
        // Check if we have an exact match
        const exactMatch = response.tags.find(tag => tag.tag.toLowerCase() === tagName.toLowerCase());
        if (exactMatch) {
          console.log(`Tag found: ${tagName} (ID: ${exactMatch.id})`);
          return exactMatch.id;
        }
      }

      // Tag doesn't exist, create it
      const tagPayload = {
        tag: {
          tag: tagName,
          tagType: 'contact',
          description: `Auto-created tag for ${tagName}`
        }
      };

      const createResponse: ActiveCampaignResponse = await this.makeRequest('tags', 'POST', tagPayload);

      if (createResponse.tag?.id) {
        console.log(`Tag created: ${tagName} (ID: ${createResponse.tag.id})`);
        return createResponse.tag.id;
      }

      return null;
    } catch (error) {
      console.error(`Error finding/creating tag ${tagName}:`, error);
      return null;
    }
  }

  private async applyTagToContact(contactId: string, tagName: string): Promise<void> {
    try {
      const tagId = await this.findOrCreateTag(tagName);

      if (!tagId) {
        console.error(`Failed to find or create tag: ${tagName}`);
        return;
      }

      const contactTagPayload = {
        contactTag: {
          contact: contactId,
          tag: tagId
        }
      };

      await this.makeRequest('contactTags', 'POST', contactTagPayload);
      console.log(`Tag ${tagName} applied to contact ${contactId}`);
    } catch (error) {
      console.error(`Error applying tag ${tagName} to contact ${contactId}:`, error);
      // Don't throw - contact creation is more important than tagging
    }
  }

  async sendNotificationEmail(userEmail: string, userName: string, businessType: string, hasPromoCode: boolean): Promise<void> {
    try {
      // Add custom fields for business tracking
      const fieldValues = [
        {
          field: 'businessType',
          value: businessType
        },
        {
          field: 'signupDate',
          value: new Date().toISOString()
        },
        {
          field: 'hasPromoCode',
          value: hasPromoCode ? 'Yes' : 'No'
        }
      ];

      const contactId = await this.addContact({
        email: userEmail,
        firstName: userName.split(' ')[0] || '',
        lastName: userName.split(' ').slice(1).join(' ') || '',
        fieldValues
      });

      console.log(`Signup notification processed for: ${userEmail}`);
      if (contactId) {
        console.log(`Contact ID: ${contactId}`);
      }
    } catch (error) {
      console.error('Error sending notification email:', error);
    }
  }
}

export const activeCampaign = new ActiveCampaignService();

server/auth.ts.content:
import express from "express";
import session from "express-session";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";
import { activeCampaign } from "./activecampaign";
import { sendDeveloperNotification } from "./sendgrid";
import { insertUserSchema, loginSchema } from "@shared/schema";
import { z } from "zod";

const pgStore = connectPg(session);

export function setupActiveCampaignTest(app: express.Application) {
  // Test endpoint for ActiveCampaign
  app.post("/api/test-activecampaign", async (req, res) => {
    try {
      console.log('ðŸ§ª Testing ActiveCampaign integration...');
      await activeCampaign.sendNotificationEmail(
        'test@example.com',
        'Test User',
        'Test Business',
        false
      );
      console.log('ðŸ§ª ActiveCampaign test SUCCESSFUL!');
      res.json({ success: true, message: 'ActiveCampaign test completed' });
    } catch (error) {
      console.error('ðŸ§ª ActiveCampaign test FAILED:', error);
      res.status(500).json({ error: error.message });
    }
  });
}

export function setupSession(app: express.Application) {
  app.use(session({
    store: new pgStore({
      conString: process.env.DATABASE_URL,
      createTableIfMissing: true,
    }),
    secret: process.env.SESSION_SECRET || "your-secret-key-change-in-production",
    resave: false,
    saveUninitialized: false,
    name: 'sessionId', // Explicit session name
    cookie: {
      secure: false, // Set to true in production with HTTPS
      httpOnly: true,
      maxAge: 30 * 24 * 60 * 60 * 1000, // 30 days
      sameSite: 'lax', // Prevent cross-site issues
    },
  }));
}

export function requireAuth(req: express.Request, res: express.Response, next: express.NextFunction) {
  console.log('requireAuth called - Session exists:', !!req.session, 'Session data:', req.session);
  if (!req.session?.userId) {
    console.log('No userId in session - rejecting');
    return res.status(401).json({ message: "Authentication required" });
  }
  console.log('requireAuth - User ID from session:', req.session.userId, 'Session ID:', req.sessionID);
  next();
}

export async function requireActiveSubscription(req: express.Request, res: express.Response, next: express.NextFunction) {
  if (!req.session?.userId) {
    return res.status(401).json({ message: "Authentication required" });
  }

  try {
    const user = await storage.getUser(req.session.userId);
    if (!user) {
      return res.status(401).json({ message: "User not found" });
    }

    // Check if user has active subscription
    if (user.subscriptionStatus === 'active') {
      return next();
    }

    // Check if user has free access (CLIENT6FREE code)
    if (user.subscriptionStatus === 'free_access' && user.subscriptionEndDate) {
      const now = new Date();
      const freeAccessEnd = new Date(user.subscriptionEndDate);

      if (now <= freeAccessEnd) {
        return next(); // Free access still active
      } else {
        // Free access expired, update status
        await storage.updateSubscriptionStatus(user.id, 'expired');
        return res.status(402).json({ 
          message: "Your 6-month free access has expired. Please subscribe to continue.",
          freeAccessExpired: true 
        });
      }
    }

    // No valid subscription - redirect to payment
    return res.status(402).json({ 
      message: "Please subscribe to access business tools.",
      subscriptionRequired: true 
    });

  } catch (error) {
    console.error('Subscription check error:', error);
    return res.status(500).json({ message: "Server error" });
  }
}

export function setupAuthRoutes(app: express.Application) {
  // Register new user
  app.post("/api/auth/register", async (req, res) => {
    try {
      const userData = insertUserSchema.parse(req.body);

      // Check if user already exists
      const existingUser = await storage.getUserByEmail(userData.email);
      if (existingUser) {
        return res.status(400).json({ message: "User already exists" });
      }

      const user = await storage.createUser(userData);
      console.log('ðŸ” User created successfully, now updating subscription status...');

      // Set inactive status - users need to subscribe to access business tools
      try {
        await storage.updateSubscriptionStatus(user.id, 'inactive');
        console.log('ðŸ” Successfully updated subscription status, now starting ActiveCampaign...');
      } catch (error) {
        console.error('âŒ Failed to update subscription status:', error);
        // Continue with ActiveCampaign integration anyway
      }

      // Send ActiveCampaign notification immediately after user creation
      console.log('ðŸ” About to start ActiveCampaign integration...');
      console.log(`âœ… Starting ActiveCampaign integration for: ${user.email}`);
      try {
        console.log('ðŸ” Calling activeCampaign.sendNotificationEmail...');
        await activeCampaign.sendNotificationEmail(
          user.email,
          user.name,
          user.businessType,
          false
        );
        console.log(`âœ… ActiveCampaign integration completed successfully for: ${user.email}`);

        console.log('ðŸ” Calling sendDeveloperNotification...');
        // Send developer notification
        await sendDeveloperNotification(
          user.email,
          user.name,
          user.businessType,
          'registration',
          'New user registered'
        );
        console.log(`âœ… Developer notification sent for: ${user.email}`);
      } catch (error) {
        console.error('âŒ ActiveCampaign integration failed:', error);
        // Don't fail registration if notification fails
      }

      // Regenerate session for new user
      req.session.regenerate(async (err) => {
        if (err) {
          console.error('Session regeneration error during registration:', err);
          return res.status(500).json({ message: "Registration failed" });
        }

        req.session.userId = user.id;
        console.log('Registration successful - User ID:', user.id, 'Session ID:', req.sessionID, 'Email:', user.email);

        req.session.save(async (err) => {
          if (err) {
            console.error('Session save error during registration:', err);
            return res.status(500).json({ message: "Registration failed" });
          }

          // Get updated user data with subscription status
          const updatedUser = await storage.getUser(user.id);
          if (!updatedUser) {
            return res.status(500).json({ message: "Failed to retrieve user data" });
          }

          res.status(201).json({
            id: updatedUser.id,
            email: updatedUser.email,
            name: updatedUser.name,
            businessType: updatedUser.businessType,
            subscriptionStatus: updatedUser.subscriptionStatus,
            subscriptionEndDate: updatedUser.subscriptionEndDate,
          });
        });
      });
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid input", errors: error.errors });
      }
      console.error("Registration error:", error);
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // Login user
  app.post("/api/auth/login", async (req, res) => {
    try {
      console.log('Login attempt received:', req.body);
      const { email, password } = loginSchema.parse(req.body);
      console.log('Parsed credentials - Email:', email, 'Password length:', password.length);

      const user = await storage.verifyPassword(email, password);
      console.log('Password verification result:', user ? `User found: ${user.id}` : 'No user found');
      if (!user) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      // Store user ID directly in session
      req.session.userId = user.id;
      console.log('LOGIN SUCCESS - User ID:', user.id, 'Session ID:', req.sessionID, 'Email:', user.email);

      // Force session save and verify
      req.session.save((err) => {
        if (err) {
          console.error('Session save failed:', err);
          return res.status(500).json({ message: "Session save failed" });
        }

        console.log('Session saved - Verifying userId stored:', req.session.userId);

        // Verify session was saved correctly
        if (!req.session.userId) {
          console.error('CRITICAL: Session userId not stored after save!');
          return res.status(500).json({ message: "Session storage failed" });
        }

        res.json({
          id: user.id,
          email: user.email,
          name: user.name,
          businessType: user.businessType,
          subscriptionStatus: user.subscriptionStatus,
          subscriptionEndDate: user.subscriptionEndDate,
        });
      });
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid input", errors: error.errors });
      }
      console.error("Login error:", error);
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // Logout user
  app.post("/api/auth/logout", (req, res) => {
    console.log('Logout request - User ID:', req.session?.userId, 'Session ID:', req.sessionID);
    req.session.destroy((err) => {
      if (err) {
        console.error('Session destroy error:', err);
        return res.status(500).json({ message: "Could not log out" });
      }
      res.clearCookie('sessionId'); // Match the session name we set
      res.clearCookie('connect.sid'); // Clear default cookie too
      console.log('Logout successful - session destroyed');
      res.json({ message: "Logged out successfully" });
    });
  });

  // Get current user
  app.get("/api/auth/user", requireAuth, async (req, res) => {
    try {
      const user = await storage.getUser(req.session.userId!);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      res.json({
        id: user.id,
        email: user.email,
        name: user.name,
        businessType: user.businessType,
        subscriptionStatus: user.subscriptionStatus,
        subscriptionEndDate: user.subscriptionEndDate,
      });
    } catch (error) {
      console.error("Get user error:", error);
      res.status(500).json({ message: "Internal server error" });
    }
  });
}

declare module "express-session" {
  interface SessionData {
    userId?: number;
  }
}

server/database-storage.ts.content:
import { 
  users, 
  hourlyRateCalculations, 
  treatments, 
  expenses,
  businesses,
  weeklyIncomes,
  incomeGoals,
  stockPurchases,
  moneyPots,
  type User, 
  type InsertUser,
  type HourlyRateCalculation,
  type InsertHourlyRateCalculation,
  type Treatment,
  type InsertTreatment,
  type Expense,
  type InsertExpense,
  type Business,
  type InsertBusiness,
  type WeeklyIncome,
  type InsertWeeklyIncome,
  type IncomeGoal,
  type InsertIncomeGoal,
  type StockPurchase,
  type InsertStockPurchase,
  type MoneyPot,
  type InsertMoneyPot
} from "@shared/schema";
import { db } from "./db";
import { eq, and, gte, lte, desc, gt } from "drizzle-orm";
import bcrypt from "bcryptjs";
import type { IStorage } from "./storage";

export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: number): Promise<User | undefined> {
    const [user] = await db.select({
      id: users.id,
      email: users.email,
      name: users.name,
      businessType: users.businessType,
      subscriptionStatus: users.subscriptionStatus,
      subscriptionEndDate: users.subscriptionEndDate,
      stripeCustomerId: users.stripeCustomerId,
      stripeSubscriptionId: users.stripeSubscriptionId,
      createdAt: users.createdAt,
      updatedAt: users.updatedAt,
    }).from(users).where(eq(users.id, id));
    return user;
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.email, email));
    return user;
  }

  async createUser(userData: InsertUser): Promise<User> {
    const hashedPassword = await bcrypt.hash(userData.password, 12);

    const [user] = await db
      .insert(users)
      .values({
        ...userData,
        password: hashedPassword,
        subscriptionStatus: "inactive", // Will be updated when Stripe subscription is created
      })
      .returning();
    return user;
  }

  async verifyPassword(email: string, password: string): Promise<User | null> {
    const [user] = await db.select().from(users).where(eq(users.email, email));
    if (!user) return null;

    const isValid = await bcrypt.compare(password, user.password);
    if (!isValid) return null;

    // Return user without password for security
    const { password: _, ...userWithoutPassword } = user;
    return userWithoutPassword as User;
  }

  async updateUserStripeInfo(userId: number, stripeCustomerId: string, stripeSubscriptionId?: string): Promise<User | undefined> {
    const [user] = await db
      .update(users)
      .set({
        stripeCustomerId,
        stripeSubscriptionId,
        updatedAt: new Date(),
      })
      .where(eq(users.id, userId))
      .returning();
    return user;
  }

  async updateSubscriptionStatus(userId: number, status: string, endDate?: Date): Promise<User | undefined> {
    const [user] = await db
      .update(users)
      .set({
        subscriptionStatus: status,
        subscriptionEndDate: endDate,
        updatedAt: new Date(),
      })
      .where(eq(users.id, userId))
      .returning();
    return user;
  }

  async getAllUsers(): Promise<User[]> {
    const allUsers = await db.select({
      id: users.id,
      email: users.email,
      name: users.name,
      businessType: users.businessType,
      subscriptionStatus: users.subscriptionStatus,
      subscriptionEndDate: users.subscriptionEndDate,
      stripeCustomerId: users.stripeCustomerId,
      stripeSubscriptionId: users.stripeSubscriptionId,
      createdAt: users.createdAt,
      updatedAt: users.updatedAt,
    }).from(users).orderBy(desc(users.createdAt));
    return allUsers;
  }

  async updateSubscriptionEndDate(userId: number, endDate: Date): Promise<User | undefined> {
    const [user] = await db
      .update(users)
      .set({
        subscriptionEndDate: endDate,
        updatedAt: new Date(),
      })
      .where(eq(users.id, userId))
      .returning();
    return user;
  }

  // Hourly rate calculation operations
  async createHourlyRateCalculation(calculation: InsertHourlyRateCalculation): Promise<HourlyRateCalculation> {
    // First, insert the new calculation
    const [result] = await db
      .insert(hourlyRateCalculations)
      .values(calculation)
      .returning();

    // Then, keep only the last 5 calculations for this user
    const allCalculations = await db
      .select()
      .from(hourlyRateCalculations)
      .where(eq(hourlyRateCalculations.userId, calculation.userId))
      .orderBy(desc(hourlyRateCalculations.createdAt));

    // If we have more than 5 calculations, delete the oldest ones
    if (allCalculations.length > 5) {
      const calculationsToDelete = allCalculations.slice(5);
      const idsToDelete = calculationsToDelete.map(calc => calc.id);

      for (const id of idsToDelete) {
        await db
          .delete(hourlyRateCalculations)
          .where(eq(hourlyRateCalculations.id, id));
      }
    }

    return result;
  }

  async getHourlyRateCalculationsByUserId(userId: number): Promise<HourlyRateCalculation[]> {
    return await db
      .select()
      .from(hourlyRateCalculations)
      .where(eq(hourlyRateCalculations.userId, userId))
      .orderBy(desc(hourlyRateCalculations.createdAt))
      .limit(5);
  }

  async getLatestHourlyRateCalculation(userId: number): Promise<HourlyRateCalculation | undefined> {
    const [calculation] = await db
      .select()
      .from(hourlyRateCalculations)
      .where(eq(hourlyRateCalculations.userId, userId))
      .orderBy(desc(hourlyRateCalculations.createdAt))
      .limit(1);
    return calculation;
  }

  // Treatment operations
  async createTreatment(treatment: InsertTreatment): Promise<Treatment> {
    // Calculate profit margin (product cost is now included in overhead cost)
    const price = parseFloat(treatment.price.toString());
    const overheadCost = parseFloat(treatment.overheadCost.toString());
    const profit = price - overheadCost;
    const profitMargin = price > 0 ? (profit / price) * 100 : 0;

    const treatmentWithMargin = {
      ...treatment,
      profitMargin: profitMargin.toFixed(2)
    };

    const [result] = await db
      .insert(treatments)
      .values(treatmentWithMargin)
      .returning();
    return result;
  }

  async getTreatmentsByUserId(userId: number): Promise<Treatment[]> {
    return await db
      .select()
      .from(treatments)
      .where(eq(treatments.userId, userId))
      .orderBy(desc(treatments.createdAt));
  }

  async updateTreatment(id: number, treatment: Partial<InsertTreatment>): Promise<Treatment | undefined> {
    const [result] = await db
      .update(treatments)
      .set(treatment)
      .where(eq(treatments.id, id))
      .returning();
    return result;
  }

  async deleteTreatment(id: number): Promise<boolean> {
    const result = await db
      .delete(treatments)
      .where(eq(treatments.id, id));
    return result.rowCount > 0;
  }

  // Expense operations
  async createExpense(expense: InsertExpense): Promise<Expense> {
    const [result] = await db
      .insert(expenses)
      .values(expense)
      .returning();
    return result;
  }

  async getExpensesByUserId(userId: number): Promise<Expense[]> {
    return await db
      .select()
      .from(expenses)
      .where(eq(expenses.userId, userId))
      .orderBy(desc(expenses.date));
  }

  async getExpensesByUserIdAndDateRange(userId: number, startDate: Date, endDate: Date): Promise<Expense[]> {
    return await db
      .select()
      .from(expenses)
      .where(
        and(
          eq(expenses.userId, userId),
          gte(expenses.date, startDate),
          lte(expenses.date, endDate)
        )
      )
      .orderBy(desc(expenses.date));
  }

  async deleteExpense(id: number): Promise<boolean> {
    const result = await db
      .delete(expenses)
      .where(eq(expenses.id, id));
    return result.rowCount > 0;
  }

  // Business operations
  async createBusiness(business: InsertBusiness): Promise<Business> {
    const [result] = await db
      .insert(businesses)
      .values(business)
      .returning();
    return result;
  }

  async getBusinessesByUserId(userId: number): Promise<Business[]> {
    return await db
      .select()
      .from(businesses)
      .where(eq(businesses.userId, userId))
      .orderBy(desc(businesses.createdAt));
  }

  async updateBusiness(id: number, business: Partial<InsertBusiness>): Promise<Business | undefined> {
    const [result] = await db
      .update(businesses)
      .set(business)
      .where(eq(businesses.id, id))
      .returning();
    return result;
  }

  async deleteBusiness(id: number): Promise<boolean> {
    const result = await db
      .delete(businesses)
      .where(eq(businesses.id, id));
    return result.rowCount > 0;
  }

  // Weekly income operations
  async createOrUpdateWeeklyIncome(weeklyIncome: InsertWeeklyIncome): Promise<WeeklyIncome> {
    const existing = await db
      .select()
      .from(weeklyIncomes)
      .where(
        and(
          eq(weeklyIncomes.businessId, weeklyIncome.businessId),
          eq(weeklyIncomes.weekStartDate, weeklyIncome.weekStartDate)
        )
      )
      .limit(1);

    if (existing.length > 0) {
      const [result] = await db
        .update(weeklyIncomes)
        .set({ weeklyTotal: weeklyIncome.weeklyTotal })
        .where(eq(weeklyIncomes.id, existing[0].id))
        .returning();
      return result;
    } else {
      const [result] = await db
        .insert(weeklyIncomes)
        .values(weeklyIncome)
        .returning();
      return result;
    }
  }

  async getWeeklyIncomesByBusinessId(businessId: number): Promise<WeeklyIncome[]> {
    return await db
      .select()
      .from(weeklyIncomes)
      .where(eq(weeklyIncomes.businessId, businessId))
      .orderBy(desc(weeklyIncomes.weekStartDate));
  }

  async getWeeklyIncomesByUserId(userId: number): Promise<WeeklyIncome[]> {
    return await db
      .select({
        id: weeklyIncomes.id,
        businessId: weeklyIncomes.businessId,
        weekStartDate: weeklyIncomes.weekStartDate,
        weeklyTotal: weeklyIncomes.weeklyTotal,
      })
      .from(weeklyIncomes)
      .innerJoin(businesses, eq(weeklyIncomes.businessId, businesses.id))
      .where(eq(businesses.userId, userId))
      .orderBy(desc(weeklyIncomes.weekStartDate));
  }

  async getWeeklyIncomeByWeek(businessId: number, weekStartDate: Date): Promise<WeeklyIncome | undefined> {
    const [result] = await db
      .select()
      .from(weeklyIncomes)
      .where(
        and(
          eq(weeklyIncomes.businessId, businessId),
          eq(weeklyIncomes.weekStartDate, weekStartDate)
        )
      )
      .limit(1);
    return result;
  }

  // Income goal operations
  async createIncomeGoal(goal: InsertIncomeGoal & { userId: number }): Promise<IncomeGoal> {
    const [result] = await db
      .insert(incomeGoals)
      .values(goal)
      .returning();
    return result;
  }

  async getIncomeGoalsByUserId(userId: number): Promise<IncomeGoal[]> {
    return await db
      .select()
      .from(incomeGoals)
      .where(eq(incomeGoals.userId, userId))
      .orderBy(desc(incomeGoals.createdAt));
  }

  async getIncomeGoalsByBusinessId(businessId: number): Promise<IncomeGoal[]> {
    return await db
      .select()
      .from(incomeGoals)
      .where(eq(incomeGoals.businessId, businessId))
      .orderBy(desc(incomeGoals.createdAt));
  }

  async updateIncomeGoal(id: number, goal: Partial<InsertIncomeGoal>): Promise<IncomeGoal | undefined> {
    const [result] = await db
      .update(incomeGoals)
      .set(goal)
      .where(eq(incomeGoals.id, id))
      .returning();
    return result;
  }

  async deleteIncomeGoal(id: number): Promise<boolean> {
    const result = await db
      .delete(incomeGoals)
      .where(eq(incomeGoals.id, id));
    return result.rowCount > 0;
  }

  // Stock purchase operations
  async createStockPurchase(purchase: InsertStockPurchase): Promise<StockPurchase> {
    const purchaseData = {
      ...purchase,
      purchaseDate: new Date(purchase.purchaseDate),
    };

    const [result] = await db
      .insert(stockPurchases)
      .values(purchaseData)
      .returning();
    return result;
  }

  async getStockPurchasesByUserId(userId: number): Promise<StockPurchase[]> {
    return await db
      .select()
      .from(stockPurchases)
      .where(eq(stockPurchases.userId, userId))
      .orderBy(desc(stockPurchases.createdAt));
  }

  async getStockPurchasesByUserIdAndDateRange(userId: number, startDate: Date, endDate: Date): Promise<StockPurchase[]> {
    return await db
      .select()
      .from(stockPurchases)
      .where(
        and(
          eq(stockPurchases.userId, userId),
          gte(stockPurchases.purchaseDate, startDate),
          lte(stockPurchases.purchaseDate, endDate)
        )
      )
      .orderBy(desc(stockPurchases.purchaseDate));
  }

  async deleteStockPurchase(id: number): Promise<boolean> {
    const result = await db
      .delete(stockPurchases)
      .where(eq(stockPurchases.id, id));
    return result.rowCount > 0;
  }

  // Password reset operations
  async setPasswordResetToken(userId: number, token: string, expires: Date): Promise<User | undefined> {
    try {
      const [user] = await db
        .update(users)
        .set({
          passwordResetToken: token,
          passwordResetExpires: expires,
          updatedAt: new Date(),
        })
        .where(eq(users.id, userId))
        .returning();
      return user;
    } catch (error) {
      console.error('Error setting password reset token:', error);
      return undefined;
    }
  }

  async getUserByResetToken(token: string): Promise<User | undefined> {
    try {
      const [user] = await db
        .select()
        .from(users)
        .where(and(
          eq(users.passwordResetToken, token),
          gt(users.passwordResetExpires, new Date())
        ));
      return user;
    } catch (error) {
      console.error('Error getting user by reset token:', error);
      return undefined;
    }
  }

  async clearPasswordResetToken(userId: number): Promise<User | undefined> {
    try {
      const [user] = await db
        .update(users)
        .set({
          passwordResetToken: null,
          passwordResetExpires: null,
          updatedAt: new Date(),
        })
        .where(eq(users.id, userId))
        .returning();
      return user;
    } catch (error) {
      console.error('Error clearing password reset token:', error);
      return undefined;
    }
  }

  async updatePassword(userId: number, hashedPassword: string): Promise<User | undefined> {
    try {
      const [user] = await db
        .update(users)
        .set({
          password: hashedPassword,
          updatedAt: new Date(),
        })
        .where(eq(users.id, userId))
        .returning();
      return user;
    } catch (error) {
      console.error('Error updating password:', error);
      return undefined;
    }
  }

  // Money pot operations
  async createMoneyPot(pot: InsertMoneyPot): Promise<MoneyPot> {
    const [result] = await db
      .insert(moneyPots)
      .values({
        ...pot,
        userId: pot.userId,
      })
      .returning();
    return result;
  }

  async getMoneyPotsByUserId(userId: number): Promise<MoneyPot[]> {
    return await db
      .select()
      .from(moneyPots)
      .where(and(eq(moneyPots.userId, userId), eq(moneyPots.isActive, true)))
      .orderBy(moneyPots.sortOrder, moneyPots.name);
  }

  async getMoneyPotsByBusinessId(businessId: number): Promise<MoneyPot[]> {
    return await db
      .select()
      .from(moneyPots)
      .where(and(eq(moneyPots.businessId, businessId), eq(moneyPots.isActive, true)))
      .orderBy(moneyPots.sortOrder, moneyPots.name);
  }

  async updateMoneyPot(id: number, pot: Partial<InsertMoneyPot>): Promise<MoneyPot | undefined> {
    try {
      const [result] = await db
        .update(moneyPots)
        .set({
          ...pot,
          updatedAt: new Date(),
        })
        .where(eq(moneyPots.id, id))
        .returning();
      return result;
    } catch (error) {
      console.error('Error updating money pot:', error);
      return undefined;
    }
  }

  async deleteMoneyPot(id: number): Promise<boolean> {
    try {
      await db
        .update(moneyPots)
        .set({ isActive: false, updatedAt: new Date() })
        .where(eq(moneyPots.id, id));
      return true;
    } catch (error) {
      console.error('Error deleting money pot:', error);
      return false;
    }
  }
}

