export async function sendEmail(params: EmailParams, ionosConfig: IonosConfig): Promise<boolean> {
  try {
    // Create transporter for Ionos email
    const transporter = nodemailer.createTransport({
      host: 'smtp.ionos.co.uk',
      port: 587,
