import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { AlertDialog, AlertDialogAction, AlertDialogCancel, AlertDialogContent, AlertDialogDescription, AlertDialogFooter, AlertDialogHeader, AlertDialogTitle, AlertDialogTrigger } from "@/components/ui/alert-dialog";
import Header from "@/components/header";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Receipt, Plus, Trash2, Calendar, ChevronDown, ChevronRight } from "lucide-react";
import { useCurrency } from "@/contexts/CurrencyContext";
import Paywall from "@/components/paywall";

const expenseSchema = z.object({
  category: z.string().min(1, "Category is required"),
  description: z.string().min(1, "Description is required"),
  amount: z.string().min(1, "Amount is required"),
  date: z.string().min(1, "Date is required"),
});

type ExpenseForm = z.infer<typeof expenseSchema>;

const categories = [
  "Adhoc",
  "Directors Wages",
  "Dividends",
  "Equipment & Maintenance",
  "Insurance",
  "Marketing & Advertising",
  "Other",
  "Products & Supplies",
  "Professional Services",
  "Rent & Utilities",
  "Spending on me (opps)",
  "Training & Education",
  "Travel & Transportation",
  "Wages"
];

export default function ExpenseTracker() {
  const { formatCurrency } = useCurrency();
  const [expandedMonths, setExpandedMonths] = useState<Set<string>>(new Set());
  const [expenseToDelete, setExpenseToDelete] = useState<number | null>(null);

  const { data: subscriptionStatus, isLoading: subscriptionLoading } = useQuery({
    queryKey: ["/api/subscription-status"],
  });

  const { toast } = useToast();

  const { data: expenses, isLoading } = useQuery({
    queryKey: ["/api/expenses"],
  });

  const form = useForm<ExpenseForm>({
    resolver: zodResolver(expenseSchema),
    defaultValues: {
      category: "",
      description: "",
      amount: "",
      date: new Date().toISOString().split('T')[0],
    },
  });

  const createExpenseMutation = useMutation({
    mutationFn: async (data: any) => {
      return apiRequest("POST", "/api/expenses", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/expenses"] });
      queryClient.invalidateQueries({ queryKey: ["/api/metrics"] });
      form.reset({
        category: "",
        description: "",
        amount: "",
        date: new Date().toISOString().split('T')[0],
      });
      toast({
        title: "Success",
        description: "Expense added successfully!",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to add expense. Please try again.",
        variant: "destructive",
      });
    },
  });

  const deleteExpenseMutation = useMutation({
    mutationFn: async (id: number) => {
      return apiRequest("DELETE", `/api/expenses/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/expenses"] });
      queryClient.invalidateQueries({ queryKey: ["/api/metrics"] });
      setExpenseToDelete(null);
      toast({
        title: "Success",
        description: "Expense deleted successfully!",
      });
    },
    onError: () => {
      setExpenseToDelete(null);
      toast({
        title: "Error",
        description: "Failed to delete expense. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleConfirmDelete = () => {
    if (expenseToDelete) {
      deleteExpenseMutation.mutate(expenseToDelete);
    }
  };

  const onSubmit = (data: ExpenseForm) => {
    createExpenseMutation.mutate(data);
  };

  const toggleMonth = (monthKey: string) => {
    const newExpanded = new Set(expandedMonths);
    if (newExpanded.has(monthKey)) {
      newExpanded.delete(monthKey);
    } else {
      newExpanded.add(monthKey);
    }
    setExpandedMonths(newExpanded);
  };

  // Group expenses by month
  const expensesByMonth = Array.isArray(expenses) ? expenses.reduce((acc: Record<string, {
    monthName: string;
    total: number;
    expenses: any[];
    categories: Record<string, number>;
  }>, expense: any) => {
    const date = new Date(expense.date);
    const monthKey = `${date.getFullYear()}-${String(date.getMonth() + 1).padStart(2, '0')}`;
    const monthName = date.toLocaleDateString('en-GB', { month: 'long', year: 'numeric' });

    if (!acc[monthKey]) {
      acc[monthKey] = {
        monthName,
        total: 0,
        expenses: [],
        categories: {}
      };
    }

    const amount = parseFloat(expense.amount.toString());
    acc[monthKey].total += amount;
    acc[monthKey].expenses.push(expense);
    acc[monthKey].categories[expense.category] = (acc[monthKey].categories[expense.category] || 0) + amount;

    return acc;
  }, {}) : {};

  // Sort months by date (most recent first)
  const sortedMonths = Object.entries(expensesByMonth).sort(([a], [b]) => b.localeCompare(a));

  const currentYear = new Date().getFullYear();
  const totalExpenses = Array.isArray(expenses) ? expenses.reduce((sum: number, expense: any) => {
    const expenseYear = new Date(expense.date).getFullYear();
    return expenseYear === currentYear ? sum + parseFloat(expense.amount.toString()) : sum;
  }, 0) : 0;

  // Show loading while checking subscription
  if (subscriptionLoading) {
    return (
      <>
        <Header 
          title="Expense Tracker" 
          description="Track and categorize your business expenses" 
        />
        <div className="flex-1 flex items-center justify-center">
          <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" aria-label="Loading"/>
        </div>
      </>
    );
  }

  // Check subscription status
  if (!(subscriptionStatus as any)?.active) {
    return (
      <>
        <Header 
          title="Expense Tracker" 
          description="Track and categorize your business expenses" 
        />
        <Paywall 
          title="Expense Tracker"
          description="Monitor and control your business costs"
          feature="expense tracking and categorization"
        />
      </>
    );
  }

  return (
    <>
      <Header 
        title="Expense Tracker" 
        description="Track and categorise your business expenses to understand your costs" 
      />

      <main className="flex-1 p-8 overflow-y-auto">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Add Expense Form */}
          <Card className="border border-slate-200">
            <CardContent className="p-6">
              <div className="mb-6">
                <div>
                  <h3 className="text-xl font-bold text-slate-800">Add Expense</h3>
                  <p className="text-slate-600 text-sm mt-1">Record a new business expense</p>
                </div>
              </div>

              <Form {...form}>
                <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                  <FormField
                    control={form.control}
                    name="category"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Category</FormLabel>
                        <Select onValueChange={field.onChange} defaultValue={field.value}>
                          <FormControl>
                            <SelectTrigger>
                              <SelectValue placeholder="Select category" />
                            </SelectTrigger>
                          </FormControl>
                          <SelectContent>
                            {categories.map((category) => (
                              <SelectItem key={category} value={category}>
                                {category}
                              </SelectItem>
                            ))}
                          </SelectContent>
                        </Select>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="description"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Description</FormLabel>
                        <FormControl>
                          <Input
                            {...field}
                            placeholder="e.g., Office rent for January"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="amount"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Amount</FormLabel>
                        <FormControl>
                          <div className="relative">
                            <span className="absolute left-3 top-1/2 transform -translate-y-1/2 text-slate-500">Â£</span>
                            <Input
                              {...field}
                              type="number"
                              step="0.01"
                              placeholder="0.00"
                              className="pl-8"
                            />
                          </div>
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="date"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Date</FormLabel>
                        <FormControl>
                          <Input
                            {...field}
                            type="date"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <Button 
                    type="submit" 
                    className="w-full bg-primary text-white hover:bg-blue-700"
                    disabled={createExpenseMutation.isPending}
                  >
                    <Plus className="h-4 w-4 mr-2" />
                    {createExpenseMutation.isPending ? "Adding..." : "Add Expense"}
                  </Button>
                </form>
              </Form>
            </CardContent>
          </Card>

          {/* Monthly Expense Summary */}
          <Card className="border border-slate-200">
            <CardContent className="p-6">
              <div className="flex items-center justify-between mb-6">
                <div>
                  <h3 className="text-xl font-bold text-slate-800">Monthly Summary</h3>
                  <p className="text-slate-600 text-sm mt-1">Expenses organised by month</p>
                </div>
                <div className="w-10 h-10 bg-green-100 rounded-lg flex items-center justify-center">
                  <Receipt className="text-success h-5 w-5" />
                </div>
              </div>

              <div className="space-y-4">
                <div className="bg-primary/10 rounded-lg p-4 text-center">
                  <p className="text-sm text-slate-600 mb-1">Total for {currentYear}</p>
                  <p className="text-2xl font-bold text-primary">{formatCurrency(totalExpenses)}</p>
                </div>

                <div className="space-y-3 max-h-96 overflow-y-auto">
                  {sortedMonths.length > 0 ? (
                    sortedMonths.map(([monthKey, monthData]) => (
                      <div key={monthKey} className="bg-slate-50 rounded-lg">
                        <div 
                          className="p-3 cursor-pointer hover:bg-slate-100 transition-colors"
                          onClick={() => toggleMonth(monthKey)}
                        >
                          <div className="flex justify-between items-center">
                            <div className="flex items-center space-x-2">
                              {expandedMonths.has(monthKey) ? (
                                <ChevronDown className="h-4 w-4 text-slate-600" />
                              ) : (
                                <ChevronRight className="h-4 w-4 text-slate-600" />
                              )}
                              <span className="font-medium text-slate-800">{monthData.monthName}</span>
                            </div>
                            <span className="font-bold text-slate-800">{formatCurrency(monthData.total)}</span>
                          </div>
                          <div className="text-xs text-slate-600 ml-6">
                            {monthData.expenses.length} expense{monthData.expenses.length !== 1 ? 's' : ''}
                          </div>
                        </div>

                        {expandedMonths.has(monthKey) && (
                          <div className="px-3 pb-3 space-y-2">
                            {Object.entries(monthData.categories).map(([category, amount]) => (
                              <div key={category} className="flex justify-between items-center py-2 px-3 bg-white rounded border-l-4 border-primary/20">
                                <span className="text-sm text-slate-700">{category}</span>
                                <span className="text-sm font-semibold text-slate-800">{formatCurrency(amount)}</span>
                              </div>
                            ))}
                          </div>
                        )}
                      </div>
                    ))
                  ) : (
                    <div className="text-center py-4 text-slate-500">
                      No expenses recorded yet
                    </div>
                  )}
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Recent Expenses */}
          <Card className="border border-slate-200">
            <CardContent className="p-6">
              <div className="flex items-center justify-between mb-6">
                <div>
                  <h3 className="text-xl font-bold text-slate-800">Recent Expenses</h3>
                  <p className="text-slate-600 text-sm mt-1">Latest transactions</p>
                </div>
                <div className="w-10 h-10 bg-purple-100 rounded-lg flex items-center justify-center">
                  <Calendar className="text-purple-600 h-5 w-5" />
                </div>
              </div>

              <div className="space-y-3 max-h-96 overflow-y-auto">
                {isLoading ? (
                  <div className="text-center py-8 text-slate-500">Loading expenses...</div>
                ) : expenses?.length ? (
                  expenses.slice(0, 10).map((expense) => (
                    <div key={expense.id} className="p-3 bg-slate-50 rounded-lg">
                      <div className="flex items-start justify-between mb-2">
                        <div className="flex-1">
                          <p className="font-medium text-slate-800">{expense.description}</p>
                          <p className="text-sm text-slate-600">{expense.category}</p>
                          <p className="text-xs text-slate-500">
                            {new Date(expense.date).toLocaleDateString()}
                          </p>
                        </div>
                        <div className="flex items-center space-x-2">
                          <span className="font-bold text-slate-800">
                            {formatCurrency(expense.amount)}
                          </span>
                          <AlertDialog>
                            <AlertDialogTrigger asChild>
                              <Button
                                variant="ghost"
                                size="sm"
                                disabled={deleteExpenseMutation.isPending}
                                className="text-red-500 hover:text-red-700 hover:bg-red-50"
                              >
                                <Trash2 className="h-4 w-4" />
                              </Button>
                            </AlertDialogTrigger>
                            <AlertDialogContent>
                              <AlertDialogHeader>
                                <AlertDialogTitle>Are you sure?</AlertDialogTitle>
                                <AlertDialogDescription>
                                  This action cannot be undone. This will permanently delete this expense from your records.
                                </AlertDialogDescription>
                              </AlertDialogHeader>
                              <AlertDialogFooter>
                                <AlertDialogCancel>No</AlertDialogCancel>
                                <AlertDialogAction
                                  onClick={() => {
                                    setExpenseToDelete(expense.id);
                                    handleConfirmDelete();
                                  }}
                                  className="bg-red-600 hover:bg-red-700"
                                >
                                  Yes
                                </AlertDialogAction>
                              </AlertDialogFooter>
                            </AlertDialogContent>
                          </AlertDialog>
                        </div>
                      </div>
                    </div>
                  ))
                ) : (
                  <div className="text-center py-8 text-slate-500">
                    No expenses recorded yet. Add your first expense!
                  </div>
                )}
              </div>
            </CardContent>
          </Card>
        </div>
      </main>
    </>
  );
}

client/src/pages/forgot-password.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { Link } from "wouter";
import { apiRequest } from "@/lib/queryClient";
import Header from "@/components/header";
import logoPath from "@assets/KatieGodfrey-Logo_Black.png";

export default function ForgotPassword() {
  const [email, setEmail] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [isSuccess, setIsSuccess] = useState(false);
  const [resetLink, setResetLink] = useState("");
  const { toast } = useToast();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!email) {
      toast({
        title: "Email required",
        description: "Please enter your email address",
        variant: "destructive",
      });
      return;
    }

    setIsLoading(true);

    try {
      const response = await apiRequest("POST", "/api/v2/auth/reset-password", { email });
      const data = await response.json();

      if (data.resetLink) {
        setResetLink(data.resetLink);
        setIsSuccess(true);
        toast({
          title: "Reset link generated",
          description: "Your password reset link is ready below",
        });
      } else {
        toast({
          title: "Reset link sent",
          description: "If that email exists, you'll receive reset instructions",
        });
      }
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to send reset link. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-pink-50 to-purple-50 dark:from-gray-900 dark:to-gray-800">
      <Header title="Forgot Password" description="Reset your account password" />

      <div className="container mx-auto px-4 py-8">
        <div className="max-w-md mx-auto">
          {/* Logo */}
          <div className="text-center mb-8">
            <img 
              src={logoPath} 
              alt="Katie Godfrey Business Coach" 
              className="h-16 mx-auto mb-4"
            />
            <h1 className="text-2xl font-bold text-gray-900 dark:text-white">
              Reset Your Password
            </h1>
          </div>

          <Card className="shadow-xl border-0">
            <CardHeader className="text-center">
              <CardTitle className="text-xl">Forgot Password?</CardTitle>
              <CardDescription>
                {isSuccess 
                  ? "Check your email for reset instructions"
                  : "Enter your email address and we'll send you a link to reset your password"
                }
              </CardDescription>
            </CardHeader>

            <CardContent>
              {!isSuccess ? (
                <form onSubmit={handleSubmit} className="space-y-4">
                  <div className="space-y-2">
                    <Label htmlFor="email">Email Address</Label>
                    <Input
                      id="email"
                      type="email"
                      placeholder="Enter your email"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                      disabled={isLoading}
                      required
                    />
                  </div>

                  <Button 
                    type="submit" 
                    className="w-full bg-pink-600 hover:bg-pink-700"
                    disabled={isLoading}
                  >
                    {isLoading ? "Sending..." : "Send Reset Link"}
                  </Button>
                </form>
              ) : (
                <div className="text-center space-y-4">
                  {resetLink ? (
                    <div className="space-y-4">
                      <div className="text-green-600 text-sm font-medium">
                        Your password reset link is ready!
                      </div>
                      <div className="bg-blue-50 dark:bg-blue-900/20 p-4 rounded-lg border border-blue-200 dark:border-blue-800">
                        <p className="text-sm text-gray-700 dark:text-gray-300 mb-2">
                          Click the link below to reset your password:
                        </p>
                        <div className="space-y-3">
                          <button
                            onClick={() => window.open(resetLink, '_self')}
                            className="w-full bg-blue-600 hover:bg-blue-700 text-white font-medium py-2 px-4 rounded-md transition-colors"
                          >
                            Reset Password Now
                          </button>
                          <div className="text-xs text-gray-600 dark:text-gray-400 break-all bg-gray-50 dark:bg-gray-800 p-2 rounded">
                            <strong>Or copy this link:</strong><br/>
                            <span className="font-mono">{resetLink}</span>
                          </div>
                        </div>
                        <p className="text-xs text-gray-500 dark:text-gray-400 mt-2">
                          This link expires in 1 hour for security
                        </p>
                      </div>
                    </div>
                  ) : (
                    <div className="text-green-600 text-sm">
                      If an account with that email exists, you'll receive reset instructions shortly.
                    </div>
                  )}
                  <div className="text-sm text-gray-600">
                    Need to try a different email address?
                  </div>
                </div>
              )}

              <div className="mt-6 text-center">
                <Link href="/login">
                  <Button variant="ghost" className="text-sm">
                    â† Back to Login
                  </Button>
                </Link>
              </div>
            </CardContent>
          </Card>

          {/* Additional help */}
          <div className="mt-6 text-center text-sm text-gray-600 dark:text-gray-400">
            <p>Need more help? Contact help at</p>
            <a href="mailto:help@salonsuccessmanager.com" className="text-pink-600 hover:underline">
              help@salonsuccessmanager.com
            </a>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/pages/hourly-rate-calculator..content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import Header from "@/components/header";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { calculateHourlyRate } from "@/lib/utils";
import { Save, Calculator, Clock, History } from "lucide-react";
import { useCurrency } from "@/contexts/CurrencyContext";
import Paywall from "@/components/paywall";

const calculatorSchema = z.object({
  monthlyExpenses: z.string().min(1, "Monthly expenses is required"),
  desiredProfit: z.string().min(1, "Desired profit is required"),
  weeklyHours: z.string().min(1, "Weekly hours is required"),
  taxRate: z.string().min(1, "Tax rate is required"),
  staffCount: z.string().optional(),
});

type CalculatorForm = z.infer<typeof calculatorSchema>;

export default function HourlyRateCalculator() {
  const { formatCurrency } = useCurrency();
  const { data: subscriptionStatus, isLoading: subscriptionLoading } = useQuery({
    queryKey: ["/api/subscription-status"],
  });

  // Fetch calculation history
  const { data: calculationHistory, isLoading: historyLoading } = useQuery({
    queryKey: ["/api/hourly-rate-calculations"],
    enabled: subscriptionStatus?.active === true,
  });

  const [calculatedResults, setCalculatedResults] = useState<{
    hourlyRate: number;
    staffTargetPerPerson: number | null;
  }>({ hourlyRate: 0, staffTargetPerPerson: null });
  const { toast } = useToast();

  // Function to parse number with commas and format for display
  const parseNumberInput = (value: string): number => {
    // Remove commas and any non-numeric characters except decimal point
    const cleanValue = value.replace(/,/g, '').replace(/[^0-9.-]/g, '');
    return parseFloat(cleanValue) || 0;
  };

  // Function to format number with commas for display
  const formatNumberWithCommas = (value: string): string => {
    // Remove any non-numeric characters except decimal point and commas
    let cleaned = value.replace(/[^0-9.,]/g, '');

    // Remove existing commas to reformat
    cleaned = cleaned.replace(/,/g, '');

    // Split into integer and decimal parts
    const parts = cleaned.split('.');
    let integerPart = parts[0];
    const decimalPart = parts[1];

    // Add commas to integer part
    if (integerPart.length > 3) {
      integerPart = integerPart.replace(/\B(?=(\d{3})+(?!\d))/g, ',');
    }

    // Reconstruct the number
    return decimalPart !== undefined ? `${integerPart}.${decimalPart}` : integerPart;
  };

  // Function to validate and format percentage inputs (max 4 digits, 1 decimal)
  const validatePercentageInput = (value: string): string => {
    // Remove any non-numeric characters except decimal point
    let cleaned = value.replace(/[^0-9.]/g, '');

    // Ensure only one decimal point
    const parts = cleaned.split('.');
    if (parts.length > 2) {
      cleaned = parts[0] + '.' + parts.slice(1).join('');
    }

    // Limit to 4 total digits and 1 decimal place
    if (cleaned.includes('.')) {
      const [integerPart, decimalPart] = cleaned.split('.');
      const limitedInteger = integerPart.slice(0, 3); // Max 3 digits before decimal
      const limitedDecimal = decimalPart.slice(0, 1); // Max 1 digit after decimal
      cleaned = limitedInteger + '.' + limitedDecimal;
    } else {
      cleaned = cleaned.slice(0, 4); // Max 4 digits if no decimal
    }

    return cleaned;
  };



  const form = useForm<CalculatorForm>({
    resolver: zodResolver(calculatorSchema),
    defaultValues: {
      monthlyExpenses: "",
      desiredProfit: "30",
      weeklyHours: "40",
      taxRate: "25",
      staffCount: "",
    },
  });

  const saveCalculationMutation = useMutation({
    mutationFn: async (data: any) => {
      return apiRequest("POST", "/api/hourly-rate-calculations", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/hourly-rate-calculations"] });
      queryClient.invalidateQueries({ queryKey: ["/api/metrics"] });
      toast({
        title: "Success",
        description: "Hourly rate calculation saved successfully!",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to save calculation. Please try again.",
        variant: "destructive",
      });
    },
  });

  const calculateRate = () => {
    const values = form.getValues();
    const monthlyExpenses = parseNumberInput(values.monthlyExpenses);
    const desiredProfit = parseNumberInput(values.desiredProfit);
    const weeklyHours = parseNumberInput(values.weeklyHours) || 40;
    const taxRate = parseNumberInput(values.taxRate);
    const staffCount = parseNumberInput(values.staffCount || "0");

    const results = calculateHourlyRate(monthlyExpenses, desiredProfit, weeklyHours, taxRate, staffCount);
    setCalculatedResults(results);
  };

  const onSubmit = (data: CalculatorForm) => {
    const calculationData = {
      monthlyExpenses: parseNumberInput(data.monthlyExpenses).toString(),
      desiredProfit: parseNumberInput(data.desiredProfit).toString(),
      weeklyHours: Math.round(parseNumberInput(data.weeklyHours)),
      taxRate: parseNumberInput(data.taxRate).toString(),
      staffCount: Math.round(parseNumberInput(data.staffCount || "0")),
      calculatedRate: calculatedResults.hourlyRate.toString(),
      staffTargetPerPerson: calculatedResults.staffTargetPerPerson?.toString() || null,
    };

    saveCalculationMutation.mutate(calculationData);
  };

  // Show loading while checking subscription
  if (subscriptionLoading) {
    return (
      <>
        <Header 
          title="Hourly Rate Calculator" 
          description="Calculate your optimal hourly rate based on expenses and profit goals" 
        />
        <div className="flex-1 flex items-center justify-center">
          <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" aria-label="Loading"/>
        </div>
      </>
    );
  }

  // Check subscription status
  if (!subscriptionStatus || !(subscriptionStatus as any).active) {
    return (
      <>
        <Header 
          title="Hourly Rate Calculator" 
          description="Calculate your optimal hourly rate based on expenses and profit goals" 
        />
        <Paywall 
          title="Hourly Rate Calculator"
          description="Calculate your optimal pricing strategy"
          feature="hourly rate calculations"
        />
      </>
    );
  }

  return (
    <>
      <Header 
        title="Hourly Rate Calculator" 
        description="Calculate your optimal hourly rate based on expenses and profit goals" 
      />

      <main className="flex-1 p-8 overflow-y-auto">
        <div className="max-w-2xl mx-auto">
          {/* Calculator Form */}
          <Card className="border border-slate-200">
            <CardContent className="p-6">
              <div className="flex items-center justify-between mb-6">
                <div>
                  <h3 className="text-xl font-bold text-slate-800">Calculate Your Rate</h3>
                  <p className="text-slate-600 text-sm mt-1">Enter your business details to calculate the optimal hourly rate. To gain the correct rate, under monthly expenses make sure you add all expenses of the business including wages and stock</p>
                </div>
                <div className="w-10 h-10 bg-blue-100 rounded-lg flex items-center justify-center">
                  <Calculator className="text-primary h-5 w-5" />
                </div>
              </div>

              <Form {...form}>
                <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                  <div className="grid grid-cols-2 gap-4">
                    <FormField
                      control={form.control}
                      name="monthlyExpenses"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Monthly Expenses</FormLabel>
                          <FormControl>
                            <div className="relative">
                              <span className="absolute left-3 top-1/2 transform -translate-y-1/2 text-slate-500">Â£</span>
                              <Input
                                {...field}
                                type="text"
                                placeholder="5,000"
                                className="pl-8"
                                onChange={(e) => {
                                  const formattedValue = formatNumberWithCommas(e.target.value);
                                  field.onChange(formattedValue);
                                  calculateRate();
                                }}
                              />
                            </div>
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="desiredProfit"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Desired Profit %</FormLabel>
                          <FormControl>
                            <div className="relative">
                              <Input
                                {...field}
                                type="text"
                                placeholder="30"
                                className="pr-8"
                                onChange={(e) => {
                                  const validatedValue = validatePercentageInput(e.target.value);
                                  field.onChange(validatedValue);
                                  calculateRate();
                                }}
                              />
                              <span className="absolute right-3 top-1/2 transform -translate-y-1/2 text-slate-500">%</span>
                            </div>
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  <div className="grid grid-cols-2 gap-4">
                    <FormField
                      control={form.control}
                      name="weeklyHours"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Working Hours/Week</FormLabel>
                          <FormControl>
                            <Input
                              {...field}
                              type="text"
                              placeholder="40"
                              onChange={(e) => {
                                const formattedValue = formatNumberWithCommas(e.target.value);
                                field.onChange(formattedValue);
                                calculateRate();
                              }}
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="taxRate"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Tax Rate %</FormLabel>
                          <FormControl>
                            <div className="relative">
                              <Input
                                {...field}
                                type="text"
                                placeholder="25"
                                className="pr-8"
                                onChange={(e) => {
                                  const validatedValue = validatePercentageInput(e.target.value);
                                  field.onChange(validatedValue);
                                  calculateRate();
                                }}
                              />
                              <span className="absolute right-3 top-1/2 transform -translate-y-1/2 text-slate-500">%</span>
                            </div>
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  <FormField
                    control={form.control}
                    name="staffCount"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Number of Staff (Optional)</FormLabel>
                        <FormControl>
                          <Input
                            {...field}
                            type="text"
                            placeholder="0"
                            onChange={(e) => {
                              const formattedValue = formatNumberWithCommas(e.target.value);
                              field.onChange(formattedValue);
                              calculateRate();
                            }}
                          />
                        </FormControl>
                        <FormMessage />
                        <p className="text-xs text-slate-500">Include yourself if you work in the business</p>
                      </FormItem>
                    )}
                  />

                  <div className="bg-slate-50 rounded-lg p-4 mt-6 space-y-3">
                    <div className="flex items-center justify-between">
                      <span className="text-slate-700 font-medium">Recommended Hourly Rate:</span>
                      <span className="text-2xl font-bold text-primary">
                        {formatCurrency(calculatedResults.hourlyRate)}
                      </span>
                    </div>
                    {calculatedResults.staffTargetPerPerson && (
                      <div className="flex items-center justify-between pt-2 border-t border-slate-200">
                        <span className="text-slate-700 font-medium">Monthly Target per Staff Member:</span>
                        <span className="text-xl font-bold text-success">
                          {formatCurrency(calculatedResults.staffTargetPerPerson)}
                        </span>
                      </div>
                    )}
                    <p className="text-slate-600 text-sm">This rate covers your expenses and achieves your profit goals</p>
                  </div>

                  <Button 
                    type="submit" 
                    className="w-full bg-primary text-white hover:bg-blue-700"
                    disabled={saveCalculationMutation.isPending || calculatedResults.hourlyRate === 0}
                  >
                    <Save className="h-4 w-4 mr-2" />
                    {saveCalculationMutation.isPending ? "Saving..." : "Save This Rate"}
                  </Button>
                </form>
              </Form>
            </CardContent>
          </Card>

          {/* Calculation History */}
          {calculationHistory && calculationHistory.length > 0 && (
            <Card className="border border-slate-200 mt-6">
              <CardContent className="p-6">
                <div className="flex items-center justify-between mb-6">
                  <div>
                    <h3 className="text-xl font-bold text-slate-800">Recent Calculations</h3>
                    <p className="text-slate-600 text-sm mt-1">Your last {calculationHistory.length} hourly rate calculations</p>
                  </div>
                  <div className="w-10 h-10 bg-green-100 rounded-lg flex items-center justify-center">
                    <History className="text-green-600 h-5 w-5" />
                  </div>
                </div>

                <div className="space-y-4">
                  {calculationHistory.map((calculation: any, index: number) => (
                    <div key={calculation.id} className="bg-slate-50 rounded-lg p-4 border">
                      <div className="flex items-center justify-between mb-2">
                        <div className="flex items-center gap-2">
                          <Clock className="h-4 w-4 text-slate-400" />
                          <span className="text-sm text-slate-500">
                            {new Date(calculation.createdAt).toLocaleDateString('en-GB', {
                              day: 'numeric',
                              month: 'short',
                              year: 'numeric',
                              hour: '2-digit',
                              minute: '2-digit'
                            })}
                          </span>
                        </div>
                        <span className="text-lg font-bold text-primary">
                          {formatCurrency(parseFloat(calculation.calculatedRate))}
                        </span>
                      </div>

                      <div className="grid grid-cols-2 md:grid-cols-4 gap-3 text-sm">
                        <div>
                          <span className="text-slate-500">Expenses:</span>
                          <div className="font-medium">{formatCurrency(parseFloat(calculation.monthlyExpenses))}</div>
                        </div>
                        <div>
                          <span className="text-slate-500">Profit:</span>
                          <div className="font-medium">{parseFloat(calculation.desiredProfit)}%</div>
                        </div>
                        <div>
                          <span className="text-slate-500">Hours:</span>
                          <div className="font-medium">{calculation.weeklyHours}/week</div>
                        </div>
                        <div>
                          <span className="text-slate-500">Tax:</span>
                          <div className="font-medium">{parseFloat(calculation.taxRate)}%</div>
                        </div>
                      </div>

                      {calculation.staffTargetPerPerson && (
                        <div className="mt-2 pt-2 border-t border-slate-200">
                          <span className="text-sm text-slate-500">Staff Target: </span>
                          <span className="font-medium text-green-600">
                            {formatCurrency(parseFloat(calculation.staffTargetPerPerson))}/month
                          </span>
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>
          )}
        </div>
      </main>
    </>
  );
}

client/src/pages/login.tsx.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Link, useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { loginSchema } from "@shared/schema";
import { z } from "zod";

type LoginForm = z.infer<typeof loginSchema>;

export default function Login() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const form = useForm<LoginForm>({
    resolver: zodResolver(loginSchema),
    defaultValues: {
      email: "",
      password: "",
    },
  });

  const loginMutation = useMutation({
    mutationFn: async (data: LoginForm) => {
      console.log('Making login request with:', data);
      const response = await apiRequest("POST", "/api/v2/auth/login", data);
      console.log('Login response received:', response.status);
      return response;
    },
    onSuccess: () => {
      console.log('Login mutation successful');
      queryClient.invalidateQueries({ queryKey: ["/api/v2/auth/user"] });
      toast({
        title: "Success",
        description: "Logged in successfully",
      });
      setLocation("/");
    },
    onError: (error: any) => {
      console.log('New login mutation error:', error);
      toast({
        title: "Login Failed",
        description: error.message || "Invalid credentials",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: LoginForm) => {
    console.log('Login form submitted with data:', data);
    loginMutation.mutate(data);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-background p-4">
      <Card className="w-full max-w-md">
        <CardHeader className="space-y-1">
          <CardTitle className="text-2xl font-bold text-center">Welcome Back</CardTitle>
          <CardDescription className="text-center">
            Sign in to your Salon Success Manager account
          </CardDescription>
        </CardHeader>
        <CardContent>
          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
              <FormField
                control={form.control}
                name="email"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Email</FormLabel>
                    <FormControl>
                      <Input
                        placeholder="Enter your email"
                        type="email"
                        {...field}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
              <FormField
                control={form.control}
                name="password"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Password</FormLabel>
                    <FormControl>
                      <Input
                        placeholder="Enter your password"
                        type="password"
                        {...field}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
              <Button
                type="submit"
                className="w-full"
                disabled={loginMutation.isPending}
              >
                {loginMutation.isPending ? "Signing in..." : "Sign In"}
              </Button>
            </form>
          </Form>

          <div className="mt-4 text-center">
            <Link href="/forgot-password">
              <Button variant="link" className="text-sm text-pink-600 hover:text-pink-700 p-0">
                Forgot your password?
              </Button>
            </Link>
          </div>

          <div className="mt-4 text-center text-sm">
            Don't have an account?{" "}
            <Link href="/register" className="text-primary hover:underline">
              Sign up
            </Link>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/money-pots.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { Plus, Edit2, Trash2, Palette } from "lucide-react";
import { z } from "zod";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { insertMoneyPotSchema } from "@shared/schema";
import type { MoneyPot } from "@shared/schema";

const moneyPotFormSchema = insertMoneyPotSchema
  .omit({ userId: true })
  .extend({
    percentage: z.string().min(1, "Percentage is required")
      .refine((val) => {
        const num = parseFloat(val);
        return !isNaN(num) && num > 0 && num <= 100;
      }, "Percentage must be between 0.01 and 100")
  });

type MoneyPotFormData = z.infer<typeof moneyPotFormSchema>;

const colorOptions = [
  { value: "#3b82f6", label: "Blue", color: "#3b82f6" },
  { value: "#ef4444", label: "Red", color: "#ef4444" },
  { value: "#10b981", label: "Green", color: "#10b981" },
  { value: "#f59e0b", label: "Orange", color: "#f59e0b" },
  { value: "#8b5cf6", label: "Purple", color: "#8b5cf6" },
  { value: "#06b6d4", label: "Cyan", color: "#06b6d4" },
  { value: "#84cc16", label: "Lime", color: "#84cc16" },
  { value: "#f97316", label: "Orange", color: "#f97316" },
];

export default function MoneyPots() {
  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false);
  const [editingPot, setEditingPot] = useState<MoneyPot | null>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: moneyPots = [] } = useQuery({
    queryKey: ["/api/money-pots"],
  });

  const createMutation = useMutation({
    mutationFn: async (data: MoneyPotFormData) => {
      const response = await fetch("/api/money-pots", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify(data),
      });
      if (!response.ok) throw new Error("Failed to create money pot");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/money-pots"] });
      setIsCreateDialogOpen(false);
      form.reset();
      toast({
        title: "Success",
        description: "Money pot created successfully",
      });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: "Failed to create money pot",
        variant: "destructive",
      });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async ({ id, data }: { id: number; data: MoneyPotFormData }) => {
      const response = await fetch(`/api/money-pots/${id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify(data),
      });
      if (!response.ok) throw new Error("Failed to update money pot");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/money-pots"] });
      setEditingPot(null);
      editForm.reset();
      toast({
        title: "Success",
        description: "Money pot updated successfully",
      });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: "Failed to update money pot",
        variant: "destructive",
      });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: number) => {
      const response = await fetch(`/api/money-pots/${id}`, {
        method: "DELETE",
        credentials: "include",
      });
      if (!response.ok) throw new Error("Failed to delete money pot");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/money-pots"] });
      toast({
        title: "Success",
        description: "Money pot deleted successfully",
      });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: "Failed to delete money pot",
        variant: "destructive",
      });
    },
  });

  const form = useForm<MoneyPotFormData>({
    resolver: zodResolver(moneyPotFormSchema),
    defaultValues: {
      name: "",
      percentage: "",
      color: "#3b82f6",
      sortOrder: 0,
    },
    mode: "onChange",
  });

  const editForm = useForm<MoneyPotFormData>({
    resolver: zodResolver(moneyPotFormSchema),
  });

  const onSubmit = (data: MoneyPotFormData) => {
    createMutation.mutate(data);
  };

  const onEdit = (pot: MoneyPot) => {
    setEditingPot(pot);
    editForm.reset({
      name: pot.name,
      percentage: pot.percentage.toString(),
      color: pot.color || "#3b82f6",
      sortOrder: pot.sortOrder || 0,
    });
  };

  const onUpdate = (data: MoneyPotFormData) => {
    if (editingPot) {
      updateMutation.mutate({ id: editingPot.id, data });
    }
  };

  const totalPercentage = (moneyPots as MoneyPot[]).reduce((sum: number, pot: MoneyPot) => sum + parseFloat(pot.percentage.toString()), 0);

  return (
    <div className="container mx-auto p-6">
      <div className="flex justify-between items-center mb-6">
        <div>
          <h1 className="text-3xl font-bold">Money Pots</h1>
          <p className="text-muted-foreground mt-2">
            Create custom categories to allocate your income. Set percentages for VAT, profit, expenses, and more.
          </p>
        </div>
        <Dialog open={isCreateDialogOpen} onOpenChange={setIsCreateDialogOpen}>
          <DialogTrigger asChild>
            <Button>
              <Plus className="w-4 h-4 mr-2" />
              Add Money Pot
            </Button>
          </DialogTrigger>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Create Money Pot</DialogTitle>
            </DialogHeader>
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                <FormField
                  control={form.control}
                  name="name"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Name</FormLabel>
                      <FormControl>
                        <Input placeholder="e.g., VAT, Profit, Equipment Fund" {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <FormField
                  control={form.control}
                  name="percentage"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Percentage (%)</FormLabel>
                      <FormControl>
                        <Input
                          type="number"
                          step="0.01"
                          min="0.01"
                          max="100"
                          placeholder="20.00"
                          {...field}
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <FormField
                  control={form.control}
                  name="color"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Color</FormLabel>
                      <Select onValueChange={field.onChange} defaultValue={field.value}>
                        <FormControl>
                          <SelectTrigger>
                            <SelectValue />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          {colorOptions.map((color) => (
                            <SelectItem key={color.value} value={color.value}>
                              <div className="flex items-center gap-2">
                                <div 
                                  className="w-4 h-4 rounded-full" 
                                  style={{ backgroundColor: color.color }}
                                />
                                {color.label}
                              </div>
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <div className="flex justify-end space-x-2">
                  <Button type="button" variant="outline" onClick={() => setIsCreateDialogOpen(false)}>
                    Cancel
                  </Button>
                  <Button type="submit" disabled={createMutation.isPending}>
                    {createMutation.isPending ? "Creating..." : "Create"}
                  </Button>
                </div>
              </form>
            </Form>
          </DialogContent>
        </Dialog>
      </div>

      {totalPercentage > 0 && (
        <Card className="mb-6">
          <CardHeader>
            <CardTitle className="text-lg">Allocation Summary</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="flex justify-between items-center mb-2">
              <span>Total Allocated:</span>
              <Badge variant={totalPercentage > 100 ? "destructive" : "default"}>
                {totalPercentage.toFixed(2)}%
              </Badge>
            </div>
            <div className="w-full bg-gray-200 rounded-full h-2">
              <div 
                className={`h-2 rounded-full ${totalPercentage > 100 ? 'bg-red-600' : 'bg-blue-600'}`}
                style={{ width: `${Math.min(totalPercentage, 100)}%` }}
              />
            </div>
            {totalPercentage > 100 && (
              <p className="text-red-600 text-sm mt-2">
                Warning: Total allocation exceeds 100%
              </p>
            )}
          </CardContent>
        </Card>
      )}

      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
        {(moneyPots as MoneyPot[]).map((pot: MoneyPot) => (
          <Card key={pot.id} className="relative">
            <CardHeader className="pb-3">
              <div className="flex items-center justify-between">
                <div className="flex items-center gap-2">
                  <div 
                    className="w-4 h-4 rounded-full" 
                    style={{ backgroundColor: pot.color || "#3b82f6" }}
                  />
                  <CardTitle className="text-lg">{pot.name}</CardTitle>
                </div>
                <div className="flex gap-1">
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => onEdit(pot)}
                  >
                    <Edit2 className="w-4 h-4" />
                  </Button>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => deleteMutation.mutate(pot.id)}
                  >
                    <Trash2 className="w-4 h-4" />
                  </Button>
                </div>
              </div>
            </CardHeader>
            <CardContent>
              <div className="text-center">
                <div className="text-3xl font-bold">{pot.percentage}%</div>
                <p className="text-muted-foreground text-sm">of income</p>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {editingPot && (
        <Dialog open={!!editingPot} onOpenChange={(open) => !open && setEditingPot(null)}>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Edit Money Pot</DialogTitle>
            </DialogHeader>
            <Form {...editForm}>
              <form onSubmit={editForm.handleSubmit(onUpdate)} className="space-y-4">
                <FormField
                  control={editForm.control}
                  name="name"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Name</FormLabel>
                      <FormControl>
                        <Input placeholder="e.g., VAT, Profit, Equipment Fund" {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <FormField
                  control={editForm.control}
                  name="percentage"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Percentage (%)</FormLabel>
                      <FormControl>
                        <Input
                          type="number"
                          step="0.01"
                          min="0.01"
                          max="100"
                          placeholder="20.00"
                          {...field}
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <FormField
                  control={editForm.control}
                  name="color"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Color</FormLabel>
                      <Select onValueChange={field.onChange} defaultValue={field.value || "#3b82f6"}>
                        <FormControl>
                          <SelectTrigger>
                            <SelectValue />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          {colorOptions.map((color) => (
                            <SelectItem key={color.value} value={color.value}>
                              <div className="flex items-center gap-2">
                                <div 
                                  className="w-4 h-4 rounded-full" 
                                  style={{ backgroundColor: color.color }}
                                />
                                {color.label}
                              </div>
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <div className="flex justify-end space-x-2">
                  <Button type="button" variant="outline" onClick={() => setEditingPot(null)}>
                    Cancel
                  </Button>
                  <Button type="submit" disabled={updateMutation.isPending}>
                    {updateMutation.isPending ? "Updating..." : "Update"}
                  </Button>
                </div>
              </form>
            </Form>
          </DialogContent>
        </Dialog>
      )}
    </div>
  );
}

