client/src/pages/profit-margin-calculato.content:
import { useState, useEffect } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import Header from "@/components/header";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { calculateTreatmentProfit, formatPercentage } from "@/lib/utils";
import { Percent, Plus, Trash2, Bath } from "lucide-react";
import { useCurrency } from "@/contexts/CurrencyContext";
import Paywall from "@/components/paywall";

const treatmentSchema = z.object({
  name: z.string().min(1, "Treatment name is required"),
  price: z.string().min(1, "Price is required"),
  duration: z.string().min(1, "Duration is required"),
  overheadCost: z.string().min(1, "Overhead cost is required"),
});

type TreatmentForm = z.infer<typeof treatmentSchema>;

export default function ProfitMarginCalculator() {
  const { formatCurrency } = useCurrency();
  const { data: subscriptionStatus, isLoading: subscriptionLoading } = useQuery({
    queryKey: ["/api/subscription-status"],
  });

  const [calculatedProfit, setCalculatedProfit] = useState({
    totalCosts: 0,
    netProfit: 0,
    profitMargin: 0,
    autoOverheadCost: 0,
  });
  const { toast } = useToast();

  const { data: treatments, isLoading } = useQuery({
    queryKey: ["/api/treatments"],
  });

  const { data: latestHourlyRate } = useQuery({
    queryKey: ["/api/hourly-rate-calculations/latest"],
  });

  const form = useForm<TreatmentForm>({
    resolver: zodResolver(treatmentSchema),
    defaultValues: {
      name: "",
      price: "",
      duration: "",
      overheadCost: "",
    },
  });

  // Auto-calculate overhead cost when duration changes
  const watchedDuration = form.watch("duration");

  useEffect(() => {
    if (watchedDuration && latestHourlyRate) {
      const duration = parseInt(watchedDuration);
      const autoOverhead = calculateOverheadCost(duration);
      if (autoOverhead > 0) {
        form.setValue("overheadCost", autoOverhead.toFixed(2));
        calculateProfit();
      }
    }
  }, [watchedDuration, latestHourlyRate]);

  const createTreatmentMutation = useMutation({
    mutationFn: async (data: any) => {
      return apiRequest("POST", "/api/treatments", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/treatments"] });
      queryClient.invalidateQueries({ queryKey: ["/api/metrics"] });
      form.reset();
      setCalculatedProfit({ totalCosts: 0, netProfit: 0, profitMargin: 0, autoOverheadCost: 0 });
      toast({
        title: "Success",
        description: "Treatment added successfully!",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to add treatment. Please try again.",
        variant: "destructive",
      });
    },
  });

  const deleteTreatmentMutation = useMutation({
    mutationFn: async (id: number) => {
      return apiRequest("DELETE", `/api/treatments/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/treatments"] });
      queryClient.invalidateQueries({ queryKey: ["/api/metrics"] });
      toast({
        title: "Success",
        description: "Treatment deleted successfully!",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to delete treatment. Please try again.",
        variant: "destructive",
      });
    },
  });

  const calculateOverheadCost = (duration: number): number => {
    if (!latestHourlyRate || !(latestHourlyRate as any).calculatedRate) return 0;
    const hourlyRate = parseFloat((latestHourlyRate as any).calculatedRate.toString());
    const hours = duration / 60; // Convert minutes to hours
    return hourlyRate * hours;
  };

  const calculateProfit = () => {
    const values = form.getValues();
    const price = parseFloat(values.price) || 0;
    const duration = parseFloat(values.duration) || 0;

    // Calculate overhead cost automatically based on duration and hourly rate
    const autoOverheadCost = calculateOverheadCost(duration);
    const manualOverheadCost = parseFloat(values.overheadCost) || 0;

    // Use manual overhead if provided, otherwise use auto-calculated
    const overheadCost = manualOverheadCost > 0 ? manualOverheadCost : autoOverheadCost;

    const netProfit = price - overheadCost;
    const profitMargin = price > 0 ? (netProfit / price) * 100 : 0;

    setCalculatedProfit({
      totalCosts: overheadCost,
      netProfit,
      profitMargin,
      autoOverheadCost
    });
  };

  const onSubmit = (data: TreatmentForm) => {
    const duration = parseInt(data.duration);
    const autoOverheadCost = calculateOverheadCost(duration);
    const manualOverheadCost = parseFloat(data.overheadCost) || 0;
    const finalOverheadCost = manualOverheadCost > 0 ? manualOverheadCost : autoOverheadCost;

    const treatmentData = {
      name: data.name,
      price: parseFloat(data.price).toString(),
      duration: duration,
      overheadCost: finalOverheadCost.toString(),
    };

    createTreatmentMutation.mutate(treatmentData);
  };

  // Show loading while checking subscription
  if (subscriptionLoading) {
    return (
      <>
        <Header 
          title="Profit Margin Calculator" 
          description="Analyze treatment profitability and optimize your pricing strategy" 
        />
        <div className="flex-1 flex items-center justify-center">
          <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" aria-label="Loading"/>
        </div>
      </>
    );
  }

  // Check subscription status
  if (!(subscriptionStatus as any)?.active) {
    return (
      <>
        <Header 
          title="Profit Margin Calculator" 
          description="Analyze treatment profitability and optimize your pricing strategy" 
        />
        <Paywall 
          title="Profit Margin Calculator"
          description="Maximize your treatment profitability"
          feature="profit margin analysis"
        />
      </>
    );
  }

  return (
    <>
      <Header 
        title="Treatment Profit Calculator" 
        description="Calculate profit margins for individual treatments and manage your service pricing" 
      />

      <main className="flex-1 p-8 overflow-y-auto">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Calculator Form */}
          <Card className="border border-slate-200">
            <CardContent className="p-6">
              <div className="flex items-center justify-between mb-6">
                <div>
                  <h3 className="text-xl font-bold text-slate-800">Add New Treatment or Course</h3>
                  <p className="text-slate-600 text-sm mt-1">Calculate profit margins for individual treatments or courses</p>
                </div>
                <div className="w-10 h-10 bg-green-100 rounded-lg flex items-center justify-center">
                  <Percent className="text-success h-5 w-5" />
                </div>
              </div>

              <Form {...form}>
                <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                  <FormField
                    control={form.control}
                    name="name"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Treatment or Course Name</FormLabel>
                        <FormControl>
                          <Input
                            {...field}
                            placeholder="e.g., Deep Cleansing Facial"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <div className="grid grid-cols-2 gap-4">
                    <FormField
                      control={form.control}
                      name="price"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Treatment Price</FormLabel>
                          <FormControl>
                            <div className="relative">
                              <span className="absolute left-3 top-1/2 transform -translate-y-1/2 text-slate-500">Â£</span>
                              <Input
                                {...field}
                                type="number"
                                placeholder="150"
                                className="pl-8"
                                onChange={(e) => {
                                  field.onChange(e);
                                  calculateProfit();
                                }}
                              />
                            </div>
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="duration"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Time Required (min)</FormLabel>
                          <FormControl>
                            <Input
                              {...field}
                              type="number"
                              placeholder="90"
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  <FormField
                    control={form.control}
                    name="overheadCost"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Overhead Cost (Auto-calculated from hourly rate)</FormLabel>
                        <FormControl>
                          <div className="relative">
                            <span className="absolute left-3 top-1/2 transform -translate-y-1/2 text-slate-500">Â£</span>
                            <Input
                              {...field}
                              type="number"
                              className="pl-8 bg-slate-50"
                              readOnly
                            />
                          </div>
                        </FormControl>
                        <FormMessage />
                        <p className="text-xs text-slate-500">
                          {latestHourlyRate ? 
                            `Automatically calculated: ${formatCurrency((latestHourlyRate as any)?.calculatedRate || 0)}/hour Ã— treatment duration` :
                            "Complete the hourly rate calculator first to enable auto-calculation"
                          }
                        </p>
                      </FormItem>
                    )}
                  />

                  <div className="bg-slate-50 rounded-lg p-4 space-y-3">
                    <div className="flex justify-between items-center">
                      <span className="text-slate-700">Total Costs:</span>
                      <span className="font-semibold text-slate-800">
                        {formatCurrency(calculatedProfit.totalCosts)}
                      </span>
                    </div>
                    <div className="flex justify-between items-center">
                      <span className="text-slate-700">Net Profit:</span>
                      <span className="font-semibold text-success">
                        {formatCurrency(calculatedProfit.netProfit)}
                      </span>
                    </div>
                    <div className="flex justify-between items-center pt-2 border-t border-slate-200">
                      <span className="text-slate-700 font-medium">Profit Margin:</span>
                      <span className="text-2xl font-bold text-success">
                        {formatPercentage(calculatedProfit.profitMargin)}
                      </span>
                    </div>
                  </div>

                  <Button 
                    type="submit" 
                    className="w-full bg-success text-white hover:bg-green-700"
                    disabled={createTreatmentMutation.isPending}
                  >
                    <Plus className="h-4 w-4 mr-2" />
                    {createTreatmentMutation.isPending ? "Adding..." : "Add to Treatment List"}
                  </Button>
                </form>
              </Form>
            </CardContent>
          </Card>

          {/* Treatment List */}
          <Card className="border border-slate-200">
            <CardContent className="p-6">
              <div className="flex items-center justify-between mb-6">
                <div>
                  <h3 className="text-xl font-bold text-slate-800">Your Treatments</h3>
                  <p className="text-slate-600 text-sm mt-1">Manage your service offerings</p>
                </div>
                <div className="w-10 h-10 bg-purple-100 rounded-lg flex items-center justify-center">
                  <Bath className="text-purple-600 h-5 w-5" />
                </div>
              </div>

              <div className="space-y-4 max-h-96 overflow-y-auto">
                {isLoading ? (
                  <div className="text-center py-8 text-slate-500">Loading treatments...</div>
                ) : (treatments as any)?.length ? (
                  (treatments as any).map((treatment: any) => (
                    <div key={treatment.id} className="p-4 bg-slate-50 rounded-lg">
                      <div className="flex items-start justify-between mb-3">
                        <div>
                          <h4 className="font-semibold text-slate-800">{treatment.name}</h4>
                          <p className="text-sm text-slate-600">{treatment.duration} minutes</p>
                        </div>
                        <div className="flex items-center space-x-2">
                          <span className="text-lg font-bold text-slate-800">
                            {formatCurrency(treatment.price)}
                          </span>
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => deleteTreatmentMutation.mutate(treatment.id)}
                            disabled={deleteTreatmentMutation.isPending}
                            className="text-red-500 hover:text-red-700 hover:bg-red-50"
                          >
                            <Trash2 className="h-4 w-4" />
                          </Button>
                        </div>
                      </div>

                      <div className="grid grid-cols-2 gap-2 text-sm">
                        <div>
                          <span className="text-slate-500">Overhead Cost:</span>
                          <p className="font-medium">{formatCurrency(treatment.overheadCost)}</p>
                        </div>
                        <div>
                          <span className="text-slate-500">Profit Margin:</span>
                          <p className="font-bold text-success">{formatPercentage(treatment.profitMargin)}</p>
                        </div>
                      </div>
                    </div>
                  ))
                ) : (
                  <div className="text-center py-8 text-slate-500">
                    No treatments added yet. Create your first treatment!
                  </div>
                )}
              </div>
            </CardContent>
          </Card>
        </div>
      </main>
    </>
  );
}

client/src/pages/register.tsx.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Link, useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { insertUserSchema } from "@shared/schema";
import { Eye, EyeOff } from "lucide-react";
import { z } from "zod";

const registerFormSchema = insertUserSchema.extend({
  confirmPassword: z.string().min(8, "Password confirmation is required"),
}).refine((data) => data.password === data.confirmPassword, {
  message: "Passwords don't match",
  path: ["confirmPassword"],
});

type RegisterForm = z.infer<typeof registerFormSchema>;

const businessTypes = [
  "Hair Salon",
  "Beauty Salon", 
  "Nail Salon",
  "Spa & Wellness",
  "Barbershop",
  "Aesthetics Clinic",
  "Training Academy",
  "Mobile Beauty",
  "Other"
];

export default function Register() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);

  const form = useForm<RegisterForm>({
    resolver: zodResolver(registerFormSchema),
    defaultValues: {
      email: "",
      password: "",
      confirmPassword: "",
      name: "",
      businessType: "",
    },
  });

  const registerMutation = useMutation({
    mutationFn: (data: RegisterForm) => {
      // Remove confirmPassword before sending to API
      const { confirmPassword, ...registerData } = data;
      return apiRequest("POST", "/api/auth/register", registerData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
      toast({
        title: "Success",
        description: "Account created successfully. Complete your subscription to access business tools.",
      });
      setLocation("/subscribe");
    },
    onError: (error: any) => {
      toast({
        title: "Registration Failed",
        description: error.message || "Failed to create account",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: RegisterForm) => {
    registerMutation.mutate(data);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-background p-4">
      <Card className="w-full max-w-md">
        <CardHeader className="space-y-1">
          <CardTitle className="text-2xl font-bold text-center">Create Account</CardTitle>
          <CardDescription className="text-center">
            Start growing your salon business today
          </CardDescription>
        </CardHeader>
        <CardContent>
          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
              <FormField
                control={form.control}
                name="name"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Full Name</FormLabel>
                    <FormControl>
                      <Input
                        placeholder="Enter your full name"
                        {...field}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
              <FormField
                control={form.control}
                name="email"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Email</FormLabel>
                    <FormControl>
                      <Input
                        placeholder="Enter your email"
                        type="email"
                        {...field}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
              <FormField
                control={form.control}
                name="password"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Password</FormLabel>
                    <FormControl>
                      <div className="relative">
                        <Input
                          placeholder="Create a password (min 8 characters)"
                          type={showPassword ? "text" : "password"}
                          {...field}
                        />
                        <Button
                          type="button"
                          variant="ghost"
                          size="sm"
                          className="absolute right-0 top-0 h-full px-3 py-2 hover:bg-transparent"
                          onClick={() => setShowPassword(!showPassword)}
                        >
                          {showPassword ? (
                            <EyeOff className="h-4 w-4 text-gray-500" />
                          ) : (
                            <Eye className="h-4 w-4 text-gray-500" />
                          )}
                        </Button>
                      </div>
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
              <FormField
                control={form.control}
                name="confirmPassword"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Confirm Password</FormLabel>
                    <FormControl>
                      <div className="relative">
                        <Input
                          placeholder="Confirm your password"
                          type={showConfirmPassword ? "text" : "password"}
                          {...field}
                        />
                        <Button
                          type="button"
                          variant="ghost"
                          size="sm"
                          className="absolute right-0 top-0 h-full px-3 py-2 hover:bg-transparent"
                          onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                        >
                          {showConfirmPassword ? (
                            <EyeOff className="h-4 w-4 text-gray-500" />
                          ) : (
                            <Eye className="h-4 w-4 text-gray-500" />
                          )}
                        </Button>
                      </div>
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
              <FormField
                control={form.control}
                name="businessType"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Business Type</FormLabel>
                    <Select onValueChange={field.onChange} value={field.value}>
                      <FormControl>
                        <SelectTrigger>
                          <SelectValue placeholder="Select your business type" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        {businessTypes.map((type) => (
                          <SelectItem key={type} value={type}>
                            {type}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <Button
                type="submit"
                className="w-full"
                disabled={registerMutation.isPending}
              >
                {registerMutation.isPending ? "Creating account..." : "Create Account"}
              </Button>
            </form>
          </Form>

          <div className="mt-4 text-center text-sm">
            Already have an account?{" "}
            <Link href="/login" className="text-primary hover:underline">
              Sign in
            </Link>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/reports.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import Header from "@/components/header";
import { formatPercentage } from "@/lib/utils";
import { useCurrency } from "@/contexts/CurrencyContext";
import { FileText, Download, Mail, TrendingUp, DollarSign, Percent, Clock } from "lucide-react";
import jsPDF from 'jspdf';
import Paywall from "@/components/paywall";
import { useToast } from "@/hooks/use-toast";
import { useState } from "react";
import type { Treatment, Expense, HourlyRateCalculation } from "@shared/schema";

export default function Reports() {
  const { toast } = useToast();
  const { formatCurrency } = useCurrency();
  const [isEmailPending, setIsEmailPending] = useState(false);

  const { data: subscriptionStatus, isLoading: subscriptionLoading } = useQuery({
    queryKey: ["/api/subscription-status"],
  });

  const { data: metrics, isLoading: metricsLoading } = useQuery({
    queryKey: ["/api/metrics"],
  });

  const { data: treatments = [], isLoading: treatmentsLoading } = useQuery<Treatment[]>({
    queryKey: ["/api/treatments"],
  });

  const { data: expenses = [], isLoading: expensesLoading } = useQuery<Expense[]>({
    queryKey: ["/api/expenses"],
  });

  const { data: hourlyCalculations = [], isLoading: calculationsLoading } = useQuery<HourlyRateCalculation[]>({
    queryKey: ["/api/hourly-rate-calculations"],
  });

  const isLoading = metricsLoading || treatmentsLoading || expensesLoading || calculationsLoading;

  // Calculate derived values for exports
  const totalExpenses = expenses.reduce((sum, expense) => sum + parseFloat(expense.amount.toString()), 0);
  const avgTreatmentPrice = treatments.length > 0 
    ? treatments.reduce((sum, t) => sum + parseFloat(t.price.toString()), 0) / treatments.length 
    : 0;

  const handleExportCSV = () => {
    const reportData = [
      ['Metric', 'Value'],
      ['Current Hourly Rate', formatCurrency(metrics?.hourlyRate || 0)],
      ['Average Profit Margin', formatPercentage(metrics?.avgProfitMargin || 0)],
      ['Monthly Revenue', formatCurrency(metrics?.monthlyRevenue || 0)],
      ['Active Treatments', (metrics?.activeTreatments || 0).toString()],
      ['Total Expenses', formatCurrency(totalExpenses)],
      ['Average Treatment Price', formatCurrency(avgTreatmentPrice)],
      [''],
      ['Treatment Breakdown', ''],
      ['Name', 'Price', 'Duration (min)', 'Profit Margin'],
      ...treatments.map(t => [
        t.name,
        formatCurrency(t.price),
        t.duration.toString(),
        t.profitMargin
      ]),
      [''],
      ['Recent Expenses', ''],
      ['Category', 'Amount', 'Date', 'Description'],
      ...expenses.slice(0, 10).map(e => [
        e.category,
        formatCurrency(e.amount),
        new Date(e.date).toLocaleDateString(),
        e.description || ''
      ])
    ];

    const csvContent = reportData.map(row => 
      row.map(cell => `"${cell}"`).join(',')
    ).join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = `business-report-${new Date().toISOString().split('T')[0]}.csv`;
    link.click();
    window.URL.revokeObjectURL(url);
  };

  const handleExportPDF = () => {
    const doc = new jsPDF();
    const pageWidth = doc.internal.pageSize.getWidth();
    const pageHeight = doc.internal.pageSize.getHeight();
    let yPosition = 20;

    // Header
    doc.setFontSize(20);
    doc.setFont('helvetica', 'bold');
    doc.text('Business Performance Report', pageWidth / 2, yPosition, { align: 'center' });
    yPosition += 15;

    doc.setFontSize(12);
    doc.setFont('helvetica', 'normal');
    doc.text(`Generated on ${new Date().toLocaleDateString()}`, pageWidth / 2, yPosition, { align: 'center' });
    yPosition += 25;

    // Key Metrics Section
    doc.setFontSize(16);
    doc.setFont('helvetica', 'bold');
    doc.text('Key Metrics', 15, yPosition);
    yPosition += 15;

    doc.setFontSize(12);
    doc.setFont('helvetica', 'normal');

    // Check if metrics exists and has properties
    if (metrics && typeof metrics === 'object') {
      const metricsData = [
        `Hourly Rate: ${formatCurrency(metrics.hourlyRate || 0)}`,
        `Avg Profit Margin: ${formatPercentage(metrics.avgProfitMargin || 0)}`,
        `Monthly Revenue: ${formatCurrency(metrics.monthlyRevenue || 0)}`,
        `Active Treatments: ${metrics.activeTreatments || 0}`
      ];

      metricsData.forEach(metric => {
        doc.text(metric, 15, yPosition);
        yPosition += 10;
      });
    }

    yPosition += 15;

    // Treatments Section
    if (treatments && treatments.length > 0) {
      doc.setFontSize(16);
      doc.setFont('helvetica', 'bold');
      doc.text('Treatment Performance', 15, yPosition);
      yPosition += 15;

      doc.setFontSize(12);
      doc.setFont('helvetica', 'normal');

      treatments.forEach(treatment => {
        if (yPosition > pageHeight - 20) {
          doc.addPage();
          yPosition = 20;
        }

        doc.text(`${treatment.name} - ${formatCurrency(treatment.price)} (${treatment.duration} min) - ${treatment.profitMargin}%`, 15, yPosition);
        yPosition += 10;
      });
    }

    yPosition += 15;

    // Recent Expenses Section
    if (expenses && expenses.length > 0) {
      doc.setFontSize(16);
      doc.setFont('helvetica', 'bold');
      doc.text('Recent Expenses', 15, yPosition);
      yPosition += 15;

      doc.setFontSize(12);
      doc.setFont('helvetica', 'normal');

      expenses.slice(0, 10).forEach(expense => {
        if (yPosition > pageHeight - 20) {
          doc.addPage();
          yPosition = 20;
        }

        const expenseDate = new Date(expense.date).toLocaleDateString();
        doc.text(`${expense.category} - ${formatCurrency(expense.amount)} - ${expenseDate} - ${expense.description || 'N/A'}`, 15, yPosition);
        yPosition += 10;
      });
    }

    // Save the PDF
    doc.save(`business-report-${new Date().toISOString().split('T')[0]}.pdf`);
  };



  const handleEmailReport = async () => {
    try {
      setIsEmailPending(true);

      const response = await fetch('/api/email-report', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
      });

      const data = await response.json();

      if (response.ok && data.success) {
        if (data.fallback && data.emailData) {
          // Use mailto fallback
          const subject = encodeURIComponent(data.emailData.subject);
          const body = encodeURIComponent(data.emailData.body);
          window.location.href = `mailto:?subject=${subject}&body=${body}`;
        } else {
          toast({
            title: "Report Sent",
            description: "Your business report has been sent to your email address.",
          });
        }
      } else {
        toast({
          title: "Email Failed",
          description: data.message || "Failed to send email report. Please try again.",
          variant: "destructive",
        });
      }
    } catch (error) {
      toast({
        title: "Error",
        description: "An error occurred while sending the report. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsEmailPending(false);
    }
  };

  // Calculate additional metrics
  const highestMarginTreatment = treatments?.length ? treatments.reduce((max, treatment) => 
    parseFloat(treatment.profitMargin) > parseFloat(max?.profitMargin || "0") ? treatment : max
  ) : null;
  const lowestMarginTreatment = treatments?.length ? treatments.reduce((min, treatment) => 
    parseFloat(treatment.profitMargin) < parseFloat(min?.profitMargin || "100") ? treatment : min
  ) : null;

  // Show loading while checking subscription
  if (subscriptionLoading) {
    return (
      <>
        <Header 
          title="Business Reports & Analytics" 
          description="Generate comprehensive business reports and insights" 
        />
        <div className="flex-1 flex items-center justify-center">
          <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" aria-label="Loading"/>
        </div>
      </>
    );
  }

  // Check subscription status
  if (!subscriptionStatus?.active) {
    return (
      <>
        <Header 
          title="Business Reports & Analytics" 
          description="Generate comprehensive business reports and insights" 
        />
        <Paywall 
          title="Business Reports & Analytics"
          description="Access detailed financial reports and insights"
          feature="business analytics and reporting"
        />
      </>
    );
  }

  return (
    <>
      <Header 
        title="Reports & Export" 
        description="View business insights and export your data for external use" 
      />

      <main className="flex-1 p-8 overflow-y-auto">
        {/* Export Actions */}
        <Card className="border border-slate-200 mb-8">
          <CardContent className="p-6">
            <div className="flex items-center justify-between mb-4">
              <div>
                <h3 className="text-xl font-bold text-slate-800">Export Options</h3>
                <p className="text-slate-600 text-sm mt-1">Download or share your business reports</p>
              </div>
              <div className="w-10 h-10 bg-blue-100 rounded-lg flex items-center justify-center">
                <FileText className="text-primary h-5 w-5" />
              </div>
            </div>

            <div className="flex flex-wrap gap-4">
              <Button onClick={handleExportPDF} className="bg-primary text-white hover:bg-blue-700">
                <Download className="h-4 w-4 mr-2" />
                Export PDF
              </Button>
              <Button onClick={handleExportCSV} variant="outline">
                <Download className="h-4 w-4 mr-2" />
                Export CSV
              </Button>
              <Button 
                onClick={handleEmailReport} 
                disabled={isEmailPending}
                variant="outline"
                className="disabled:opacity-50"
              >
                <Mail className="h-4 w-4 mr-2" />
                {isEmailPending ? 'Sending...' : 'Email Report'}
              </Button>
            </div>
          </CardContent>
        </Card>

        {/* Business Summary */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <Card className="border border-slate-200">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-slate-600 text-sm font-medium">Current Hourly Rate</p>
                  <p className="text-2xl font-bold text-slate-800 mt-1">
                    {isLoading ? "..." : formatCurrency(metrics?.hourlyRate || 0)}
                  </p>
                </div>
                <div className="w-12 h-12 bg-blue-100 rounded-lg flex items-center justify-center">
                  <Clock className="text-primary h-6 w-6" />
                </div>
              </div>
            </CardContent>
          </Card>

          <Card className="border border-slate-200">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-slate-600 text-sm font-medium">Avg Profit Margin</p>
                  <p className="text-2xl font-bold text-slate-800 mt-1">
                    {isLoading ? "..." : `${metrics?.avgProfitMargin || 0}%`}
                  </p>
                </div>
                <div className="w-12 h-12 bg-green-100 rounded-lg flex items-center justify-center">
                  <Percent className="text-success h-6 w-6" />
                </div>
              </div>
            </CardContent>
          </Card>

          <Card className="border border-slate-200">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-slate-600 text-sm font-medium">Monthly Revenue</p>
                  <p className="text-2xl font-bold text-slate-800 mt-1">
                    {isLoading ? "..." : formatCurrency(metrics?.monthlyRevenue || 0)}
                  </p>
                </div>
                <div className="w-12 h-12 bg-amber-100 rounded-lg flex items-center justify-center">
                  <DollarSign className="text-warning h-6 w-6" />
                </div>
              </div>
            </CardContent>
          </Card>

          <Card className="border border-slate-200">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-slate-600 text-sm font-medium">Total Expenses</p>
                  <p className="text-2xl font-bold text-slate-800 mt-1">
                    {isLoading ? "..." : formatCurrency(totalExpenses)}
                  </p>
                </div>
                <div className="w-12 h-12 bg-red-100 rounded-lg flex items-center justify-center">
                  <TrendingUp className="text-red-500 h-6 w-6" />
                </div>
              </div>
            </CardContent>
          </Card>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Treatment Analysis */}
          <Card className="border border-slate-200">
            <CardContent className="p-6">
              <h3 className="text-xl font-bold text-slate-800 mb-6">Treatment Analysis</h3>

              {isLoading ? (
                <div className="text-center py-8 text-slate-500">Loading treatment data...</div>
              ) : treatments?.length ? (
                <div className="space-y-6">
                  <div className="bg-slate-50 rounded-lg p-4">
                    <h4 className="font-semibold text-slate-800 mb-3">Treatment Summary</h4>
                    <div className="grid grid-cols-2 gap-4 text-sm">
                      <div>
                        <span className="text-slate-600">Total Treatments:</span>
                        <p className="font-bold text-slate-800">{treatments.length}</p>
                      </div>
                      <div>
                        <span className="text-slate-600">Average Price:</span>
                        <p className="font-bold text-slate-800">{formatCurrency(avgTreatmentPrice)}</p>
